import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useTranslation } from 'react-i18next'
import Select from "react-select";
import { useLocation, useHistory } from 'react-router-dom'
import LoadingOverlay from 'react-loading-overlay';

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Table,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"

import { CoreServices,BackendServices } from '../../services';

const AccountMovementsHistory = (props) => {

  const { t, i18n } = useTranslation();
  
  const [dataRows, setDataRows] = useState([]);
  const [isActiveLoading, setIsActiveLoading] = useState(false);

  const [apiServiceBackend, setapiServiceBackend] = useState(new BackendServices());
  const [apiCoreServices, setCoreServices] = useState(new CoreServices());

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    fetchData();        
  }, []);

  function fetchData() {
    loadData();
  }

  //cargar lista de facilidades
  function loadData(){
    apiCoreServices.getApcInfo(props.clientDocumentId)
        .then((data) => {
              console.log("getApcInfo",data);
            if (data !== null && data !== undefined) {
                var rows = data.map((data,index) => (
                    <tr key={ index }>
                        <td>{ data.source }</td>
                      <td>{ data.year }</td>
                      <td>{ data.month }</td>
                      <td>{ data.initialAmount }</td>
                      <td>{ data.actualAmount }</td> 
                    </tr>)
                  );
                  setDataRows(rows);
            }          
      }).catch((error) => { });    
  }

  

  return (

    <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
        
        <Card>
          
                  <Row>
                    <Col md="6">
                      <h5 className="card-sub-title">{t("AccountMovementsHistory")}</h5>
                    </Col>                    
                  </Row>
                  
          <CardBody>
          
        <Row>
        <Col md="12">
          <div className="table-responsive">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Source")}</th>
                  <th>{t("Year")}</th>
                  <th>{t("Month")}</th>
                  <th>{t("InitialAmount")}</th>
                  <th>{t("ActualAmount")}</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
              {dataRows.length > 0?dataRows:null}
              </tbody>
            </Table>
          </div>
        </Col>        
      </Row>
          </CardBody>
        </Card>          
           
    </LoadingOverlay>

  )
}

AccountMovementsHistory.propTypes = {
  clientDocumentId:PropTypes.string
}

//export default (withTranslation()(DatosGenerales));
export default AccountMovementsHistory;
