/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types'
import { useLocation, useHistory } from "react-router-dom";
import { Link } from "react-router-dom"

import {
  Card,CardHeader,
  CardBody,Row,
  Col,
  Button,
  CardFooter,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../../components/Common/Breadcrumb"

//i18n
import { useTranslation, withTranslation } from "react-i18next"

import DatosGenerales from "./DatosGenerales"
import Fideicomiso from "./Fideicomiso"
import Fideicomitente from "./Fideicomitente"
import ComisioinesFiduciarias from "./ComisioinesFiduciarias"
import BeneficiarioSecundario from "./BeneficiarioSecundario"
import Conclusiones from "./Conclusiones"
import ApiServicesFideicomitentes from "../../../../services/Fideicomiso/ApiServicesFideicomitentes"
import ModalCloseOptions from "./ModalCloseOptions";
import ModalAdvanceOptions from "./ModalAdvanceOptions";
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import * as url from "../../../../helpers/url_helper";
import { AvForm } from "availity-reactstrap-validation";
import ModalPrevicualizarIGR from "../../4_InformeGestionReforzado/FormularioIGR/previsualizarIGR";

const PantallaBusqueda = props => {
  const { t, i18n } = useTranslation();
  const apiBack = new BackendServices();
  const history = useHistory();
  const [ModalPrevisualizardata, settoogleModalPrevisualizar] = useState(false);

  const location = useLocation()
  const { selectedData } = location;
  const [dataLocation, setData] = useState(location.data);
  const [dataList, setDataList] = useState([]);
  const [datosUsuario, setdatosUsuario] = useState(null);
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalAdvanceOptions, setDisplayModalAdvanceOptions] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [dataTrust, setdataTrust] = useState(null);
  const [dataFeduciario, setdataFeduciario] = useState({
    "transactId": 0,
    "structuring": "",
    "facilityType": "",
    "creditLineRot": "",
    "creditLineNoRot": "",
    "declineLoan": "",
    "status": true
  });

  const comisionesFiduciariaRef = React.useRef();
  const conclusionesRef = React.useRef();

  const [locationData, setLocationData] = useState(null);
  //Servicios
  const [backendServices, setbackendServices] = useState(new BackendServices());
  const [bpmServices, setbpmServices] = useState(new BpmServices());
  const [coreServices, setcoreServices] = useState(new CoreServices());

  const [mainDebtor, setmainDebtor] = useState(null);

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        fetchData(location.data);
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        fetchData(result);
      }
    }
  }, []);

  //Caraga Inicial de datos
  function fetchData(locationData) {

    //chequear si la tarea no ha sido iniciada
    bpmServices.checkAndStartTask(locationData)
      .then((iniresult) => {
        if (iniresult === false) {
          history.push(url.URL_DASHBOARD);
        }
      })

    loadUserProspect(locationData.transactionId)
    // Read Api Service data
    searchData(null)
    cargarDatosPrincipales()
  }

  function loadUserProspect(transactionId) {
    // consultarDeudorPrincipal
    backendServices.consultPrincipalDebtor(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setmainDebtor(data);
        }
      });
  }


  function searchData(values) {
    console.log(values);
    const api = new ApiServicesFideicomitentes()
    api.getResultadosFideicomitentes(values)
      .then((data) => {
        setDataList(data);
      })
      .catch((error) => {
        console.error('Error fetching documents', error);
      });

  }
  function guardarDatos() {
    if (!comisionesFiduciariaRef.current.getFormValidation()) {
      comisionesFiduciariaRef.current.validateForm();
      return false;
    }
    if (!conclusionesRef.current.getFormValidation()) {
      conclusionesRef.current.validateForm();
      return false;
    }
    let dataSend = {
      "transactId": Number(dataLocation.transactionId),
      "structuring": comisionesFiduciariaRef.current.dataReturn.listaRevisionSelect,
      "facilityType": comisionesFiduciariaRef.current.dataReturn.facilityType,
      "creditLineRot": comisionesFiduciariaRef.current.dataReturn.creditLineRot,
      "creditLineNoRot": comisionesFiduciariaRef.current.dataReturn.creditLineNoRot,
      "declineLoan": comisionesFiduciariaRef.current.dataReturn.declineLoan,
    }
    let dato = {
      "transactId": Number(dataLocation.transactionId),
      "conclusions": conclusionesRef.current.dataReturn.conclusiones,
      "status": true
    }
    apiBack.guardarTipoFiduciarioYOtrosDatos(dataSend).then(resp => {
      if (resp !== null && resp !== undefined) {
        apiBack.actualizarNumeroFideicomiso(dato).then(resp => {
          if (resp !== null && resp !== undefined) {
            setsuccessSave_dlg(true)
          } else {
            seterror_dlg(true);
          }
        }).catch(error => {
          seterror_dlg(true);
        })
      } else {
        seterror_dlg(true);
      }
    }).catch(error => {
      seterror_dlg(true);
    })
    // event.preventDefault();
    // if (errors.length > 0) {
    //   return;
    // }
  }
  function cargarDatosPrincipales() {
    const api = new BackendServices()
    api.consultPrincipalDebtor(dataLocation.transactionId).then(resp => {
      setdatosUsuario(resp)
    })
    api.consultarTipoFiduciarioYOtrosDatos(dataLocation.transactionId).then(resp => {
      console.log(resp);
      setdataFeduciario(resp)
    })
    api.consultarNumeroFideicomiso(dataLocation.transactionId).then(resp => {
      if (resp == undefined) {
        api.nuevoNumeroFideicomiso({
          "transactId": Number(dataLocation.transactionId),
          "conclusions": " "
        }).then(response => {
          setdataTrust(response[0])
        })
      } else {
        setdataTrust(resp[0])
      }
    })
  }


  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
  }

  //Modal Opciones al Salir
  function toggleModalModalAdvanceOptions() {
    setDisplayModalAdvanceOptions(!displayModalAdvanceOptions);
  }

  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option, values = null) {
    switch (option) {
      case 1: {

        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 3: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 4: {
        let descripcion = values.modalAdvanceOptionDescription;
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        api.abortProcess(descripcion).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
    }

  }
  function toogleModalPrevisualizar() {
    settoogleModalPrevisualizar(!ModalPrevisualizardata);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  
  return (
    <React.Fragment>
      <div className="page-content">

        <Breadcrumbs title={t("CommercialCredit")} breadcrumbItem={t("Assignescrownumber")} />
        <Card>
        <CardHeader>
              <Row>
                <Col>
                  <h4 className="card-title">{t("Assignescrownumber")}</h4>
                  <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                </Col>
              </Row> 
              <Row>
                <Col sm={6}>
                  <h5 className="card-title">{mainDebtor != null ? (mainDebtor.typePerson === "1" ? (mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2) : (mainDebtor.name)): ""} </h5>
                </Col>
                <Col sm={6}>
                  <h5 className="card-title" style={{ textAlign: "right" }}>{t("Tramit")}:{" "}#{locationData?.transactionId}</h5>
                </Col>
              </Row>                           
            </CardHeader>
        
          <CardBody>
            {/* <DatosGenerales /> */}
            <h4 className="card-title">{datosUsuario != null ? datosUsuario.name + " " + datosUsuario.name2 + " " + datosUsuario.lastName + " " + datosUsuario.lastName2 : ""}    #{dataLocation.transactionId}</h4>
            <Button color="info" type="button" style={{ margin: '5px' }} onClick={() => { toogleModalPrevisualizar() }}><i className="mdi mdi-eye mdi-12px"></i> Previsualizar IGR</Button>
            <Fideicomiso />
            <Fideicomitente results={dataList} />
            {/* <ComisioinesFiduciarias dataFeduciario={dataFeduciario} ref={comisionesFiduciariaRef} /> */}
            <BeneficiarioSecundario />
            <Conclusiones dataTrust={dataTrust} ref={conclusionesRef} />
          </CardBody>
          <CardFooter style={{ textAlign: "right" }}>
            <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {t("Exit")}</Button>
            <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { guardarDatos() }} ><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {t("Continue")}</Button>
          </CardFooter>
        </Card>
        <ModalPrevicualizarIGR isOpen={ModalPrevisualizardata} toggle={() => { toogleModalPrevisualizar() }} />
        <ModalAdvanceOptions onSaveProcess={onSaveProcess} isOpen={displayModalAdvanceOptions} toggle={() => { toggleModalModalAdvanceOptions() }} />
        {successSave_dlg ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
      </div>
    </React.Fragment>
  );
}

PantallaBusqueda.propTypes = {
  selectedId: PropTypes.any,
  onClose: PropTypes.func.isRequired
}

export default PantallaBusqueda
