/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Card,
  CardBody,
  CardHeader,
  Button,
  Label,
  Table
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"

//i18n
import { withTranslation } from "react-i18next"

import ModalFideicomitente from "./ModalFideicomitente"


const Fideicomitente = (props) => {

    const [showModalFideicomitente, setShowModalFideicomitente] = useState(false);

  

    //const dataFideicomitente = [
      //  { id: 1, name:"Nombre Apellido", address: "dirección", email: "fideicomitente@gmail.com",phone: "62454545"},
        //{ id: 2, name:"Nombre Apellido", address: "dirección", email: "fideicomitente@gmail.com",phone: "62454545"},
        //{ id: 3, name:"Nombre Apellido", address: "dirección", email: "fideicomitente@gmail.com",phone: "62454545"},
    //];


    const dataFideicomitenteRows = props.results.map((data) => (
        <tr key={ data.id }>
            <td>{ data.client.name}</td>
          <td>{ data.client.address }</td>
          <td>{ data.client.email }</td>
          <td>{ data.client.phone }</td>
          <td style={{textAlign:"right"}}>
                    <Button type="button" color="link" className="btn btn-link"  title={props.t("Edit")}>  <i className="mdi mdi-circle-edit-outline mdi-24px"></i></Button>
                    <Button type="button" color="link" className="btn btn-link"  title={props.t("Delete")}> <i className="mdi mdi-file-remove-outline mdi-24px"></i>
 </Button>
              </td>
        </tr>)
      );
      
      //abrimos modal para adjunar archivos
      function toggleShowModalFideicomitente() {
        setShowModalFideicomitente(!showModalFideicomitente);
        removeBodyCss()
      }

      function removeBodyCss() {
        document.body.classList.add("no_padding")
      }

 
  return (
     
    <React.Fragment>
        
      
    <Row>
            <Col md="12">
            
            <p className="card-title-desc"></p>
        <h4 className="card-title">{ props.t("Settlors") }</h4>
        <p className="card-title-desc"></p>
      
            <Table className="table mb-0">
            <thead className="table-light">
              <tr>
                <th>{props.t("Name")}</th>
                <th>{props.t("Address")}</th>
                <th>{props.t("Email")}</th>
                <th>{props.t("Phone")}</th>
                <th style={{textAlign:"right"}}>
                <Button type="button" color="link" className="btn btn-link" onClick={() => { toggleShowModalFideicomitente() }} title={props.t("Add")}> <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
 </Button>
                </th>
              </tr>              
            </thead>
            <tbody>
              { dataFideicomitenteRows}
            </tbody>
          </Table>
     
            </Col>
          </Row>     
          
                
          <ModalFideicomitente isOpen={showModalFideicomitente} toggle={() => { toggleShowModalFideicomitente() }} />     

        </React.Fragment>
    
    
  );




  
}


Fideicomitente.propTypes = {
   results: PropTypes.array.isRequired, //Resultado de la busqueda
}

export default (withTranslation()(Fideicomitente))
