  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"


const ModalFlujoCaja = (props) => {
  const { t, i18n } = useTranslation();

  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {

    event.preventDefault();
    if (errors.length > 0) {
      return;
    }

    if (props.tipo == "DINERO") {
      props.guardarDatos(values, props.tipo);
    }
    if (props.tipo == "EDINERO") {
      values.cashFlowDolarId = props.jsonSow.cashFlowDolarId;
      values.status = true;
      props.actualizarDatos(values, props.tipo);
    }
    if (props.tipo == "DEUDAS") {
      props.guardarDatos(values, props.tipo);
    }
    if (props.tipo == "EDEUDAS") {
      values.cashFlowDebtServiceId = props.jsonSow.cashFlowDebtServiceId;
      values.status = true;
      props.actualizarDatos(values, props.tipo);
    }
    if (props.tipo == "INGREFACTURA") {
      values.cashFlowInvoiceId = props.jsonSow.cashFlowInvoiceId;
      values.status = true;
      props.guardarDatos(values, props.tipo);
    }
    if (props.tipo == "EINGREFACTURA") {
      values.cashFlowInvoiceId = props.jsonSow.cashFlowInvoiceId;
      values.status = true;
      props.actualizarDatos(values, props.tipo);
    }
    if (props.tipo == "ECOBRANZA") {
      values.cashFlowCollectiond = props.jsonSow.cashFlowCollectiond;
      values.status = true;
      props.actualizarDatos(values, props.tipo);
    }
    if (props.tipo == "COBRANZA") {
      values.cashFlowCollectiond = props.jsonSow.cashFlowCollectiond;
      values.status = true;
      props.guardarDatos(values, props.tipo);
    }
    if (props.tipo == "EEGRESO") {
      values.cashFlowOutcomeId = props.jsonSow.cashFlowOutcomeId;
      values.description = "";
      values.februray = values.february;
      values.status = true;
      props.actualizarDatos(values, props.tipo);
    }
    if (props.tipo == "EGRESO") {
      values.description = "";
      values.cashFlowOutcomeId = props.jsonSow.cashFlowOutcomeId;
      values.februray = values.february;
      values.status = true;
      props.guardarDatos(values, props.tipo);
    }
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">

        <h5 className="modal-title mt-0">{props.tipo == "DINERO" ? t("CashFlow") : (props.tipo == "EDINERO" ? t("CashFlow") : (props.tipo == "DEUDA" ? t("DebtServicing") : (props.tipo == "EDEUDA" ? t("DebtServicing") : (props.tipo == "COBRANZA" ? t("CollectionsITBMSNotIncluded") : t("CashFlow")))))}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  {props.tipo == "EEGRESO" || props.tipo == "EGRESO" ? null :
                    <Row>
                      <Col md="6">
                        <div className="mb-3">
                          <Label htmlFor="description">{t("Description")}</Label>
                          <AvField
                            className="form-control"
                            type="text"
                            name="description"
                            value={props.jsonSow.description}
                            validate={{
                              required: { value: true, errorMessage: t("Required Field") },
                            }}
                            id="description"
                          />
                        </div>
                      </Col>
                    </Row>
                  }
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="january">{t("January")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="january"
                          value={props.jsonSow.january}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="january"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="february">{t("February")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="february"
                          value={props.jsonSow.february}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="february"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="march">{t("March")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="march"
                          value={props.jsonSow.march}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="march"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="april">{t("April")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="april"
                          value={props.jsonSow.april}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="april"
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="may">{t("May")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="may"
                          value={props.jsonSow.may}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="may"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="june">{t("June")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="june"
                          value={props.jsonSow.june}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="june"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="july">{t("July")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="july"
                          value={props.jsonSow.july}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="july"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="august">{t("August")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="august"
                          value={props.jsonSow.august}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="august"
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="september">{t("September")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="september"
                          value={props.jsonSow.september}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="september"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="october">{t("October")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="october"
                          value={props.jsonSow.october}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="october"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="november">{t("November")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="november"
                          value={props.jsonSow.november}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="november"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="december">{t("December")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="december"
                          value={props.jsonSow.december}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          id="december"
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "DINERO" || props.tipo == "DEUDAS" || props.tipo == "INGREFACTURA" || props.tipo == "COBRANZA" || props.tipo == "EGRESO" ? t("Save") : t("Edit")}
                    </Button> : null}
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal>
  );
};

ModalFlujoCaja.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default ModalFlujoCaja;
