import React, { useEffect, useState } from "react"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"
import { useTranslation, withTranslation } from "react-i18next"

import {
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"
import { Link } from "react-router-dom"

// UI Components
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import {
  ChangeSummary, CorporateExposure, CorporateExposureClient,
  FacilityList, FinancialSummary,
  GeneralSection
} from "../../../components/PropuestaCreditoComercial"

// Services
import { BackendServices, CoreServices, BpmServices, } from "../../../services";
import ApiServiceBackend from "../../../services/BackendServices/Services";
import {
  ServicioExposicionCorporativa,
} from "../../../services/PropuestaCredito";
import LoadingOverlay from "react-loading-overlay"
import { translationHelpers, formatCurrency } from '../../../helpers';

// Services
const coreServices = new CoreServices();
const backServices = new BackendServices();


const CommercialCreditProposalPage = (props) => {


  const history = useHistory();
  const location = useLocation();

  const [cc, c, tr] = translationHelpers('commercial_credit', 'common', 'translation');
  const { t, i18n } = useTranslation();

  const sumaryData = {
    client: {
      name: 'Nombre Deudor'
    },
    financialStatus: {
      headers: ['Auditados Dic-18', 'Auditados Dic-19', 'Auditados Dic-20', 'Proyecciones Dic-21'],
      items: [
        {
          label: 'Clasificación SAFI',
          data: ['-', '-', '5:6 Riesgo', '-']
        },
        {
          label: 'Crecimiento',
          data: ['-', '-3.4%', '-12.5%', '0.0%']
        },
        {
          label: 'Costo de Ventas',
          data: ['(1550)66.2%', '(1510)66.8%', '(1360)68.8%', '(1360)68.8%']
        },
        {
          label: 'Beneficio Mutuo en Operaciones',
          data: ['(790)66.2%', '(751)66.8%', '(618)68.8%', '(618)68.8%']
        },
        {
          label: 'Margen Bruto',
          data: ['', '', '', '']
        }
      ]
    },
    balances: {
      active: {
        title: t('Active Balance'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      },
      passive: {
        title: t('Passive Balance and Capital'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      },
      application: {
        title: t('Origin and Application Status'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      },
      indicators: {
        title: t('Indicators'),
        headers: [t('Period Date'), '', '', ''],
        items: [
          [t('Months'), '', '', ''],
          [t('Auditor'), '', '', ''],
          [t('Balance Type'), '', '', ''],
          [t('Figures Types'), '', '', ''],
          [t('Coins Types'), '', '', ''],
          [t('Exchange Rates'), '', '', '']
        ]
      }
    },
    debts: {
      short: {
        items: [
          {
            bank: 'Capital Bank',
            purpose: 'Linea de Crédito',
            approvedAmount: 200,
            balanceLastYear: 200,
            balanceLast6Months: 154,
            balanceForecast: 200,
            rate: 7.0,
            surety: 'Fianza'
          },
          {
            bank: 'St Georges',
            purpose: 'Linea de Crédito',
            approvedAmount: 320,
            balanceLastYear: 200,
            balanceLast6Months: 436,
            balanceForecast: 450,
            rate: 7.5,
            surety: 'Fianza'
          },
          {
            bank: 'St Georges',
            purpose: 'TDC',
            approvedAmount: 11,
            balanceLastYear: 11,
            balanceLast6Months: 0,
            balanceForecast: 11,
            rate: null,
            surety: ''
          }
        ]
      },
      long: {
        items: [
          {
            bank: 'Banesco',
            purpose: 'Préstamo',
            approvedAmount: 126,
            balanceLastYear: 0,
            balanceLast6Months: 0,
            balanceForecast: 126,
            rate: 6.5,
            surety: 'Bien Inmueble'
          }
        ]
      }
    },
    history: {
      headers: [
        t('# days', { days: 30 }),
        t('# days', { days: 60 }),
        t('# days', { days: 90 }),
        t('# days', { days: 120 }),
        t('# days', { days: 150 }),
        t('# days', { days: 180 }),
        t('# days', { days: 210 }),
        t('# days', { days: 240 }),
        t('# days', { days: 270 }),
        t('# days', { days: 300 }),
        t('# days', { days: 330 }),
        t('+# days', { days: 331 }),
        t('Totals')
      ],
      data: [
        {
          name: 'Banesco',
          data: [100, 80, 80, 80, 100, 80, 80, 80, 100, 80, 80, 200, 0]
        },
        {
          name: 'Banesco',
          data: [100, 80, 80, 80, 100, 80, 80, 80, 100, 80, 80, 200, 0]
        }
      ]
    }
  }

  const [proposal, setProposal] = useState(null);
  const [changeSummaryItems, setChangeSummaryItems] = useState([]);
  const [facilityListItems, setFacilityListItems] = useState([]);
  const [identificationList, setIdentificationList] = useState([]);
  const [financialSummary, setFinancialSummary] = useState(sumaryData);
  const [prospect, setProspect] = useState({});
  const [isActiveLoading, setIsActiveLoading] = useState(true);
  const [personId, setpersonId] = useState(null);

  // const customerNumberT24 =location.data.customerId;
  // const customerNumberT24 = '600133774';

  useEffect(() => {
    getProspect();
  }, []);

  function fetchProposal() {

    setProposal({
      proposal: {
        id: 1010,
        revisionType: 'Bimensual',
        preapproved: false,
        minSib: 'Provision',
        relatedPart: false,
        approvalLevel: 'Nivel',
        flowType: 'Normal',
        applicationDate: '24-sep-2021',
        lastApplicationDate: '24-sep-2021',
        nextRevisionDate: '24-sep-2021',
        expirationDate: '24-sep-2021'
      },
      client: {
        number: '3223213',
      },
      economicGroup: {
        number: '32132006176',
        name: 'Grupo Tova SA',
        business: 'Actividad seleccionada'
      },
      risk: {
        countryClass: 'Panamá',
        classification: 'Medio',
        countryRisk: 'Nicaragua'
      },
      accountExecutive: {
        name: 'Elibeth'
      },
      responsibleUnit: {
        name: 'Unidad'
      }
    });

    setChangeSummaryItems([
      { description: 'Incremento / Disminución (Exposición)' },
      { description: 'Plazos: Fac 1' },
      { description: 'Tasa: Fac 1' },
      { description: 'Cuotas' },
      { description: 'Garantías' },
      { description: 'Covenats' },
      { description: 'Fiadores / Codeudores' },
      { description: 'Otras condiciones' },
    ]);

    setFinancialSummary({
      client: {
        name: 'Nombre Deudor'
      },
      financialStatus: {
        headers: ['Auditados Dic-18', 'Auditados Dic-19', 'Auditados Dic-20', 'Proyecciones Dic-21'],
        items: [
          {
            label: 'Clasificación SAFI',
            data: ['-', '-', '5:6 Riesgo', '-']
          },
          {
            label: 'Crecimiento',
            data: ['-', '-3.4%', '-12.5%', '0.0%']
          },
          {
            label: 'Costo de Ventas',
            data: ['(1550)66.2%', '(1510)66.8%', '(1360)68.8%', '(1360)68.8%']
          },
          {
            label: 'Beneficio Mutuo en Operaciones',
            data: ['(790)66.2%', '(751)66.8%', '(618)68.8%', '(618)68.8%']
          },
          {
            label: 'Margen Bruto',
            data: ['', '', '', '']
          }
        ]
      },
      balances: {
        active: {
          title: t('Active Balance'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        passive: {
          title: t('Passive Balance and Capital'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        application: {
          title: t('Origin and Application Status'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        },
        indicators: {
          title: t('Indicators'),
          headers: [t('Period Date'), '', '', ''],
          items: [
            [t('Months'), '', '', ''],
            [t('Auditor'), '', '', ''],
            [t('Balance Type'), '', '', ''],
            [t('Figures Types'), '', '', ''],
            [t('Coins Types'), '', '', ''],
            [t('Exchange Rates'), '', '', '']
          ]
        }
      },
      debts: {
        short: {
          items: [
            {
              bank: 'Capital Bank',
              purpose: 'Linea de Crédito',
              approvedAmount: 200,
              balanceLastYear: 200,
              balanceLast6Months: 154,
              balanceForecast: 200,
              rate: 7.0,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'Linea de Crédito',
              approvedAmount: 320,
              balanceLastYear: 200,
              balanceLast6Months: 436,
              balanceForecast: 450,
              rate: 7.5,
              surety: 'Fianza'
            },
            {
              bank: 'St Georges',
              purpose: 'TDC',
              approvedAmount: 11,
              balanceLastYear: 11,
              balanceLast6Months: 0,
              balanceForecast: 11,
              rate: null,
              surety: ''
            }
          ]
        },
        long: {
          items: [
            {
              bank: 'Banesco',
              purpose: 'Préstamo',
              approvedAmount: 126,
              balanceLastYear: 0,
              balanceLast6Months: 0,
              balanceForecast: 126,
              rate: 6.5,
              surety: 'Bien Inmueble'
            }
          ]
        }
      }
    });

  }

  function loadIdentifications() {

    return new Promise((resolve, reject) => {
      backServices.consultarCatalogoTipoIdentificacion()
        .then((data) => {
          if (data !== null && data !== undefined) {
            setIdentificationList(data);
          }
          resolve();
        })
        .catch((error) => {
          resolve();
          console.error('api error: ', error);
        });
    })
  }

  function handleReturn() {
    history.push(url.URL_DASHBOARD);
  }

  function handleClose() {
    history.push(url.URL_DASHBOARD);
  }

  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }

  function getProspect() {
    Promise.allSettled([
      //backServices.consultarUsuarioProspecto(location.data.transactionId),
      //backServices.consultProspectUser(location.data.transactionId),
      backServices.consultPrincipalDebtor(location.data.transactionId),
      loadIdentifications()
    ]).then((resp) => {
      const [{ value: PrincipalDebtor }] = resp;
      console.log(resp, location, PrincipalDebtor);
      setIsActiveLoading(false);
      let customerNumberT24 = PrincipalDebtor.customerNumberT24;
      setProspect(customerNumberT24);
      loadFacilitiesList(customerNumberT24);
      setpersonId(PrincipalDebtor.personId);
    }).catch(err => {
      // console.log(err);
    });

    // backServices.consultProspectUser(location.data.transactionId).then(async (resp) => {
    //   console.log(resp);
    //   setIsActiveLoading(false)
    //   // customerNumberT24 = resp.customerNumberT24;
    //   setProspect(customerNumberT24);
    //   loadFacilitiesList(customerNumberT24);
    //   fetchProposal();
    // });

    // backServices.consultPrincipalDebtor(location.data.transactionId).then(async (resp) => {
    //   setpersonId(resp.personId);
    // });

  }


  function loadFacilitiesList(idcliente) {
    return new Promise(function (resolve, reject) {
      coreServices.getFacilities(idcliente)
        .then(resp => {
          setFacilityListItems(resp);
          resolve();
        })
        .catch(err => {
          resolve();
        });
    })
  }

  function handleSaveFacility(newItem) {
    const newFacilities = [...facilityListItems, newItem];
    setFacilityListItems(newFacilities);
  }

  return (
    <div className="page-content">

      <Breadcrumbs title={t("Commercial Credit")} breadcrumbItem={tr("Credit Analysis")} />
      <Card>
            <CardBody>
              <h4 className="card-title">{mainDebtor != null ? mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2 : ""}    #{locationData?.transactionId}</h4>
              <h4 className="card-title">{t("Credit Proposal")}</h4>
              <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
              <Row>
                <Col md={3}>
                  <Nav pills className="flex-column">
                    <NavLink className={classnames({ active: activeTab === 1 })}
                      onClick={() => {
                        toggleTab(1)
                      }}>
                      {t("General Data")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 2 })}
                      onClick={() => {
                        toggleTab(2)
                      }}>
                      {t("CorporateExhibition")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 3 })}
                      onClick={() => {
                        toggleTab(3)
                      }}>
                      {t("Change Summary")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 4 })}
                      onClick={() => {
                        toggleTab(4)
                      }}>
                      {t("FacilitiesList")}
                    </NavLink>
                    <NavLink className={classnames({ active: activeTab === 5 })}
                      onClick={() => {
                        toggleTab(5)
                      }}>
                      {t("Check List")}
                    </NavLink>
                  </Nav>
                </Col>
                <Col md={9}>
                  <LoadingOverlay active={isActiveLoading} spinner text={t("Processinginformation")}>
                    <TabContent activeTab={activeTab} className="body">
                      <TabPane tabId={1}>
                        <DatosGeneralesPropuesta dataGlobal={dataGlobal} ref={creditProposalRef}
                          title={t("General Data")}
                          values={datosGenerales} />
                      </TabPane>
                      <TabPane tabId={2}>
                        <ExposicionCorportativa title={t("CorporateExhibition")} customerNumberT24={mainDebtor?.customerNumberT24} />
                        <ExposicionCorporativaClientes title={t("CorporateExhibitionClient")} customerNumberT24={mainDebtor?.customerNumberT24} />
                      </TabPane>
                      <TabPane tabId={3}>
                        <ChangeSummary dataGlobal={dataGlobal} ref={ResumenCambiosRef} title={t("Change Summary")} items={changeSummaryItems} />
                      </TabPane>
                      <TabPane tabId={4}>
                        {validarDatos ?
                          <ListaFacilidad title={t("Facility List")}
                            items={facilityListItems}
                            editMode={true}
                            ref={FacilidadesRef}
                            dataGlobal={dataGlobal}
                            onSaveFacility={handleSaveFacility}
                            identificationList={identificationList} /> : null}
                      </TabPane>
                      <TabPane tabId={5}>
                        <CheckList title={t("Check List")} items={checkListItems}
                          onSaveItem={handleSaveCheckListItem} />
                      </TabPane>
                    </TabContent>
                  </LoadingOverlay>
                </Col>
              </Row>
            </CardBody>
            <CardFooter className="text-end">
                            
                    <Row>
                      <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
                      <Button color="info" type="button" style={{ margin: '5px' }} onClick={() => { toogleModalPrevisualizar() }}><i className="mdi mdi-eye mdi-12px"></i> Previsualizar</Button>
              
              {activeTab > 1 && (
                <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { wizardRef.current.prev() }}>
                  <i className="mdi mdi-arrow-left-bold mid-12px"></i>&nbsp;
                  {t("Previous")}
                </Button>)
              }
              {activeTab < 5 && (
                <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => {
                  saveDataCreditProposal(); setIsActiveLoading(true); setTimeout(() => {
                    saveDataCreditProposal();
                  }, 1000);
                }}>
                  {t("Next")}&nbsp;
                  <i className="mdi mdi-arrow-right-bold mid-12px"></i>
                </Button>)
              }
                      </Col>
                    </Row>

                    <Row>
                      <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
                        <Button color="danger" type="button" style={{ margin: '5px' }} onClick={() => { showModalEndProcess() }}><i className="mdi mdi-cancel mid-12px"></i> {t("Cancel")}</Button>
                        <Button color="primary" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(OPTs.OPT_SALVAPARCIAL) }}><i className="mdi mdi-content-save-outline mid-12px"></i> {t("Making")}</Button>
                        <Button color="success" type="button" style={{ margin: '5px' }} 
                          onClick={() => {
                            onSaveProcess(OPTs.OPT_SALVAFULL); 
                            setIsActiveLoading(true); 
                            setTimeout(() => {
                              onSaveProcess(OPTs.OPT_SALVAFULL);
                            }, 1000);
                        }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {t("Continue")}</Button>
                      </Col>
                    </Row>

            </CardFooter>
          </Card>
          

      <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
        <Card>
          <Row>
            <Col xl="12">
              <GeneralSection proposal={proposal} prospect={prospect} />
            </Col>
          </Row>
          <Row>
            <Col xl="12">
              <CorporateExposure prospect={prospect} title={t("CorporateExhibition")} customerNumberT24={prospect} />
            </Col>
          </Row>

          <Row>
            <Col xl="12">
              <CorporateExposureClient title={t("CorporateExhibitionClient")} customerNumberT24={prospect} />
            </Col>
          </Row>

          <Row>
            <Col xl="12">
              <ChangeSummary title={t("SummaryOfChanges")} items={changeSummaryItems} />
            </Col>
          </Row>

          <Row>
            <Col xl="12">
              <FacilityList title={t("Facility List")}
                items={facilityListItems}
                editMode={true}
                onSaveFacility={handleSaveFacility}
                identificationList={identificationList} />
            </Col>
          </Row>

          <div className="m-3">
            <FinancialSummary client={financialSummary.client}
              financialStatus={financialSummary.financialStatus}
              balances={financialSummary.balances}
              debts={financialSummary.debts}
              debtorId={personId}
            />
          </div>

          <Row className="my-2">
            <Col xl="12 text-end">
              <Button id="btnClose" color="dark" type="button" style={{ margin: '5px' }} onClick={handleClose}>
                {t("Close")}
              </Button>
              <Button id="btnSave" color="success" type="button" style={{ margin: '5px' }} onClick={handleReturn}>
                {t("Return")}
              </Button>
              <Link to={{ pathname: "/creditocomercial/analisiscredito/informefinanciero", data: location.data }} className="btn btn-success" style={{ margin: '5px' }}>{t("Financial Report")}</Link>

            </Col>
          </Row>
        </Card>

      </LoadingOverlay >

    </div>
  );

}


export default CommercialCreditProposalPage;
