import React, { useEffect, useRef } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Row,
    Col,
    Form,
    Container,
    Card,
    Button,
} from "react-bootstrap";
import Select from 'react-select'
import { AvField, AvForm, AvGroup } from 'availity-reactstrap-validation';
import { Label } from 'reactstrap';
import Flatpickr from "react-flatpickr";

import checkNumber from '../../../helpers/checkNumber';
import * as OPTs from "../../../helpers/options_helper"
import { useFormQuoter } from './Hooks/useFormQuoter';
import LoadingOverlay from 'react-loading-overlay';
import TableResultQuoter from './TableResultQuoter';

const initialForm = {
    // // common fields
    personType: "Natural",
    // idType: "",
    // idnumber: "",
    // // client data
    // firstname: "",
    // name: "",
    // secondname: "",
    // firstlastname: "",
    // secondlastname: "",
    // birthDate: "",
    // nationality: "",
    // phoneNumber: "",
    // email: "",
    // // quoter data
    loanType: "compuesto",
    amountDebt: 0,
    anualRate: 0,
    effectiveRate: 0,
    paymentPeriod: 1,
    definedPayment: "No",
    customerDefinedPayment: 0,
    term: 0,
    feciPercentage: "No",
    feciRate: 0,
    gracePeriodType: "NA",
    gracePeriod: 0,
    // banking: "",
    // legalExpenses: 0,
    // financialTrust: 0,
}

export const FormData = (props) => {
    const { t, i18n } = useTranslation();

    const {
        // form,
        onChange,
        loader,
    } = props

    const {
        form,
        hdlTest,
        hdlOnKeyUpRealNumber,
        hdlOnKeyUpPercentageNumber,
        hdlOnSubmit,
        hdlOnInvalidSubmit,
        hdlOnValidSubmit,
        hdlOnChange,
        disableFeci,
        disableDefinedFee,
        disableTerm,
        disableGracePeriod,
        loading,
        response,
    } = useFormQuoter(initialForm)
    console.log("form:", form);

    const options = [
        { label: 'Natural', value: 'Natural' },
        { label: 'Jurídica', value: 'Juridica' }
    ]
    const optionsIdType = [
        { value: '1', label: 'RUC' },
        { value: '2', label: 'RIF' },
        { value: '3', label: 'Cédula' },
    ]

    const optionsNationality = [
        { value: '1', label: 'Cuba' },
        { value: '2', label: 'Panamá' },
        { value: '3', label: 'Venezuela' }
    ]

    const optionsFormatDate = {
        dateFormat: OPTs.FORMAT_DATE,
        maxDate: new Date().setFullYear(new Date().getFullYear() - 18),
        // defaultDate: selectClient !== undefined ? new Date(moment(selectClient.birthDate, 'YYYY-MM-DD').format()) : new Date()
    }

    const optionsConfirmation = [
        { label: 'Sí', value: 'Si' },
        { label: 'No', value: 'No', }
    ]
    const optionsPayments = [
        { label: 'Mensual', value: 1 },
        { label: 'Bimensual', value: 2 },
        { label: 'Trimestral', value: 3 },
        { label: 'Cuatrimestral', value: 4 },
        { label: 'Semestral', value: 6 },
        { label: 'Anual', value: 12 },
    ]
    const optionsGracePeriodType = [
        { label: 'Total', value: 't' },
        { label: 'Parcial', value: 'p' },
    ]
    const optionsBanking = [
        { label: 'International Banking', value: '1' },
        { label: 'Fuerza en venta', value: '2' },
    ]
    const optionsTypeLoan = [
        { label: 'Compuesto', value: 'compuesto' },
        { label: 'Abono', value: 'abono', }
    ]

    return (
        <>
            <AvForm onSubmit={hdlOnValidSubmit}>
                {/* onValidSubmit={hdlOnValidSubmit} */}
                {/* onInvalidSubmit={hdlOnInvalidSubmit} */}
                <Card>
                    <Container>
                        <Card.Title className='mt-3'>
                            {t("ClientData")}
                        </Card.Title>
                        <Row>
                            <Col md={6} lg={4}>
                                <div className="mb-3">
                                    <Label htmlFor="personType">{t("PersonType")}</Label>
                                    <Select
                                        options={options}
                                        id="personType"
                                        name="personType"
                                        defaultValue={options[0]}
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={4}>
                                <div className="mb-3">
                                    <Label htmlFor="idType">{t("IdType")}</Label>
                                    <Select
                                        id="idType"
                                        name="idType"
                                        options={optionsIdType}
                                        defaultValue={optionsIdType[2]}
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={12} lg={4}>
                                <div className="mb-3">
                                    <Label htmlFor="idnumber">{t("IdNumber")}</Label>
                                    <AvField
                                        id="idnumber"
                                        name="idnumber"
                                        value={form.name}
                                        validate={{ required: { value: true } }}
                                        errorMessage={t("Required Field")}
                                        options={optionsIdType}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            {
                                form.personType === "Natural"
                                &&
                                <>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="firstname">{t("FirstName")}</Label>
                                            <AvField
                                                type='text'
                                                id="firstname"
                                                name="firstname"
                                                value={form.name}
                                                validate={{ required: { value: true } }}
                                                errorMessage={t("Required Field")}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="secondname">{t("SecondName")}</Label>
                                            <AvField
                                                id="secondname"
                                                name="secondname"
                                                value={form.name}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="firstlastname">{t("FirstLastName")}</Label>
                                            <AvField
                                                id="firstlastname"
                                                name="firstlastname"
                                                value={form.name}
                                                validate={{ required: { value: true } }}
                                                errorMessage={t("Required Field")}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="secondlastname">{t("SecondLastName")}</Label>
                                            <AvField
                                                id="secondlastname"
                                                name="secondlastname"
                                                value={form.name}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                </>
                            }
                        </Row>
                        <Row>
                            {
                                form.personType === "Natural"
                                &&
                                <Col md={6} lg={3}>
                                    <AvGroup>
                                        <div className="mb-3">
                                            <Label htmlFor="birthDate">{t("Date of birth")}</Label>
                                            <Flatpickr
                                                id="birthDate"
                                                name="birthDate"
                                                value={form.name}
                                                placeholder={OPTs.FORMAT_DATE_SHOW}
                                                options={optionsFormatDate}
                                                className="form-control d-block"
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </AvGroup>
                                </Col>
                            }
                            {
                                form.personType !== "Natural"
                                &&
                                <Col md={6} lg={3}>
                                    <div className="mb-3">
                                        <Label htmlFor="name">{t("SocialReason")}</Label>
                                        <AvField
                                            type='text'
                                            id="name"
                                            name="nameJ"
                                            value={form.name}
                                            validate={{ required: { value: true } }}
                                            errorMessage={t("Required Field")}
                                            onChange={onChange}
                                        />
                                    </div>
                                </Col>
                            }
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="nationality">{t("Nationality")}</Label>
                                    <Select
                                        id="nationality"
                                        name="nationality"
                                        value={form.name}
                                        options={optionsNationality}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="phoneNumber">{t("PhoneNumber")}</Label>
                                    <AvField
                                        id="phoneNumber"
                                        name="phoneNumber"
                                        value={form.name}
                                        onKeyPress={(e) => { return checkNumber(e) }}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="email">{t("Email")}</Label>
                                    <AvField
                                        type="email"
                                        id="email"
                                        name="email"
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            {/* QUOTER DATA */}
                            <Card.Title className='mt-5'>
                                {t("QuoterData")}
                            </Card.Title>
                        </Row>
                        <Row>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="loanType">{t("Quoter type")}</Label>
                                    <Select
                                        id="loanType"
                                        name="loanType"
                                        defaultValue={optionsTypeLoan[0]}
                                        value={form.name}
                                        options={optionsTypeLoan}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="amountDebt">{t("Amount Debt")}</Label>
                                    <AvField
                                        id="amountDebt"
                                        name='amountDebt'
                                        value={form.name}
                                        validate={{ required: { value: true } }}
                                        errorMessage={t("Required Field")}
                                        onChange={hdlOnChange}
                                        onKeyUp={hdlOnKeyUpRealNumber}
                                        onKeyPress={(e) => { return checkNumber(e) }}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="anualRate">{t("Annual rate (%)")}</Label>
                                    <AvField
                                        id="anualRate"
                                        name="anualRate"
                                        value={form.name}
                                        validate={{ required: { value: true } }}
                                        errorMessage={t("Required Field")}
                                        onChange={hdlOnChange}
                                        onKeyUp={hdlOnKeyUpPercentageNumber}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="effectiveRate">{t("Effective rate (%)")}</Label>
                                    <AvField
                                        id="effectiveRate"
                                        name="effectiveRate"
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="paymentPeriod">{t("Payments")}</Label>
                                    <Select
                                        id="paymentPeriod"
                                        name="paymentPeriod"
                                        defaultValue={optionsPayments[0]}
                                        value={form.name}
                                        options={optionsPayments}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="definedPayment">{t("Defined fee")}</Label>
                                    <Select
                                        id="definedPayment"
                                        name="definedPayment"
                                        options={optionsConfirmation}
                                        defaultValue={optionsConfirmation[1]}
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="customerDefinedPayment">{t("Defined fee value")}</Label>
                                    <AvField
                                        id="customerDefinedPayment"
                                        name="customerDefinedPayment"
                                        value={form.name}
                                        validate={{ required: { value: !disableDefinedFee } }}
                                        errorMessage={t("Required Field")}
                                        disabled={disableDefinedFee}
                                        onChange={hdlOnChange}
                                        onKeyUp={hdlOnKeyUpRealNumber}
                                        onKeyPress={(e) => { return checkNumber(e) }}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="term">{t("Term (months)")}</Label>
                                    <AvField
                                        id="term"
                                        name="term"
                                        value={form.name}
                                        validate={{ required: { value: !disableTerm } }}
                                        errorMessage={t("Required Field")}
                                        disabled={disableTerm}
                                        onChange={hdlOnChange}
                                        onKeyPress={(e) => { return checkNumber(e) }}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="feciSelect">{t("Feci")}</Label>
                                    <Select
                                        id="feciSelect"
                                        name="feciSelect"
                                        defaultValue={optionsConfirmation[1]}
                                        value={form.name}
                                        options={optionsConfirmation}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="feciRate">{t("Feci amount")}</Label>
                                    <AvField
                                        id="feciRate"
                                        name="feciRate"
                                        value={form.name}
                                        validate={{ required: { value: !disableFeci } }}
                                        errorMessage={t("Required Field")}
                                        disabled={disableFeci}
                                        onChange={hdlOnChange}
                                        onKeyUp={hdlOnKeyUpPercentageNumber}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="gracePeriodType">{t("Grace period type")}</Label>
                                    <Select
                                        id="gracePeriodType"
                                        name="gracePeriodType"
                                        defaultValue={optionsGracePeriodType[1]}
                                        value={form.name}
                                        options={optionsGracePeriodType}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="gracePeriod">{t("Grace period")}</Label>
                                    <AvField
                                        id="gracePeriod"
                                        name="gracePeriod"
                                        value={form.name}
                                        validate={{ required: { value: true } }}
                                        errorMessage={t("Required Field")}
                                        // disabled={disableGracePeriod}
                                        onChange={hdlOnChange}
                                        onKeyPress={(e) => { return checkNumber(e) }}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            <Col md={12} lg={6}>
                                <div className="mb-3">
                                    <Label htmlFor="banking">{t("Banking")}</Label>
                                    <Select
                                        id="banking"
                                        name="banking"
                                        value={form.name}
                                        options={optionsBanking}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="legalExpenses">{t("Legal expenses")}</Label>
                                    <AvField
                                        id="legalExpenses"
                                        name="legalExpenses"
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="financialTrust">{t("Trust")}</Label>
                                    <AvField
                                        id="financialTrust"
                                        name="financialTrust"
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            <Col className='d-grid mt-5'>
                                <Button
                                    size='lg'
                                    className='float-end'
                                    type='submit'
                                // onClick={handleFormSubmit}
                                >
                                    {t("Calculate")}
                                </Button>
                            </Col>
                        </Row>
                    </Container>
                </Card>
            </AvForm>
            <LoadingOverlay
                active={loading}
                spinner={true}
                text={t("Calculating payment plan")}
            >
            </LoadingOverlay>
            {
                response !== null
                &&
                <TableResultQuoter
                    paymentPlan={response}
                />
            }

        </>

    )
}
