import React, { useState, useEffect } from "react"
import { withTranslation } from "react-i18next"
import  translationHelper  from "../../../helpers/translation-helper"
import { translationHelpers } from "../../../helpers"

import { useLocation,useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"

import {
  Row,
  Col,
  Button,
  Card,
  CardBody
} from "reactstrap"

//Import Breadcrumb
import  Breadcrumbs  from "../../../components/Common/Breadcrumb"
import FormFilter from "./aceptacionCliente/FormFilter"
import SearchResults from "./aceptacionCliente/SearchResults"
import ApiServicesAceptacionCliente from "../../../services/AceptacionCliente/ApiServicesAceptacionCliente"

const InboxPage = (props) => {

  const [showResults, setShowResults] = useState(false);
  const [dataList, setDataList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [displayClienteModal, setDisplayClienteModal] = useState(false);

  //nuevo informe abrimos modal para buscar y seleccionar cliente
  function toggleClienteModal() {
    setDisplayClienteModal(!displayClienteModal);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }



  //buscamos las Propuestas de Credito
  function searchData(values) {
    console.log(values);
    const api = new ApiServicesAceptacionCliente()
    api.getResultadosBandejaEntradaAceptacionCliente(values)
      .then((data) => {
       
        console.log(data);
        setDataList(data);
        setShowResults(true);
        
      })
      .catch((error) => {
    
        console.error('Error fetching documents', error);
      });
 

  }





  return (

<React.Fragment>      

<div className="page-content">
  
<Breadcrumbs title={ props.t("CommercialCredit") } breadcrumbItem={ props.t("ClientAcceptance") } />


    <Card>
      <CardBody> 
          <FormFilter onSubmit={searchData} />
            {showResults && (
              <SearchResults results={dataList} />
            )}
      </CardBody>
    </Card>          
  
    </div>
</React.Fragment>

  );

}


export default (withTranslation()(InboxPage));
