  return (
     <div className="page-content">
        
        <Breadcrumbs title={t("CommercialCredit")} breadcrumbItem={t("PreRequest")} />

        <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
        
        <Card>
            <CardBody>
            
          
            <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("PersonType")}</th>
                  <th>{t("FullName")}</th>
                  <th>{t("DBO")}</th>
                  <th>{t("Participation")}</th>
                  <th>{t("YearsExprience")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setdataAcciones({
                      transactId: 0,
                      personType: "",
                      identificationType: "",
                      identificationNumber: "",
                      shareholderFirstName: "",
                      shareholderMiddleName: "",
                      shareholderFirstSurname: "",
                      shareholderSecondSurname: "",
                      nationality: {
                        code: "",
                        name: ""
                      },
                      birthDate: 0,
                      participation: 0,
                      yearsExperience: 0
                    }); settipo("guardar"); toggleShowModelAttachment()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {shareholderRows}
              </tbody>
            </Table>
          </Col>
        </Row>


            </CardBody>
        </Card>       
      </LoadingOverlay>       

      </div>      
    
  )
}

export default PreRequest
