    }

    backendServices.actualizarFacilidadPropCred(datos).then(resp => {
      if (resp !== null && resp !== undefined) {
        setViewSeccion(!ViewSeccion);
        setsuccessSave_dlg(true);
      } else {
        seterror_dlg(true);
      }
      setIsActiveLoading(false)
    }).catch(err => {
