    getLocalAccessToken() {
      const currentToken = JSON.parse(localStorage.getItem("token"));
      return currentToken;
    }
  
    updateLocalAccessToken(token) {
      localStorage.setItem("token", JSON.stringify(token));
    }
  
    removeLocalAccessToken() {
      localStorage.removeItem("token");
