import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"
import Select from "react-select";

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

const ModalEmpresasRelacionadas = (props) => {
  const { t, i18n } = useTranslation();
  const [campoRequeridoRelacion, setcampoRequeridoRelacion] = useState(false);
  const [relacion, setrelacion] = useState(null);
  const [relacionSelect, setrelacionSelect] = useState(undefined);
  const relacionData=[{ label: "Cliente", value: "Cliente" }, { label: "Proveedor", value: "Proveedor" }, { label: "Cliente-Proveedor", value: "Cliente-Proveedor" }]
  React.useEffect(() => {
    // Read Api Service data
    var defaultVal = null;
    setrelacionSelect(undefined)
    try {
        if (relacionData.length > 0 && props.jsonEmpresaRelacionada.relationship !== null && relacionSelect === undefined) {
            defaultVal = relacionData.find(x => x.value === props.jsonEmpresaRelacionada.relationship);
            if (defaultVal !== undefined) {
                setrelacionSelect(defaultVal);
            }
        }
    }
    catch (err) { }

}, [props]);
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    if (relacion == null) {
      setcampoRequeridoRelacion(true);
      return;
    } else {
      setcampoRequeridoRelacion(false);
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.relationship=relacion;
    if(props.tipo=="guardar"){
      props.addEmpresaRelacionada(values,props.tipo);
      
    }else{
      values.companyId=Number(props.jsonEmpresaRelacionada.companyId);
      props.addEmpresaRelacionada(values,props.tipo);

    }
  }
  function cambio(e) {
    setrelacion(e.value)
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("RelatedCompanies")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="name">{t("FullName")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="name"
                          id="name"
                          value={props.jsonEmpresaRelacionada.name}
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>
                    </Col>
                    <Col md="2">
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="activity">{t("Activity")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="activity"
                          id="activity"
                          value={props.jsonEmpresaRelacionada.activity}
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="sectorExperience">{t("YearsInTheSector")}</Label>
                        <AvField
                          className="form-control"
                          type="number"
                          name="sectorExperience"
                          id="sectorExperience"
                          value={props.jsonEmpresaRelacionada.sectorExperience}
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>
                    </Col>
                    <Col md="5">
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="relationship">{t("Relation")}</Label>
                        <Select
                          onChange={(e) => { setrelacionSelect(relacionData.find(x => x.value === e.value)); cambio(e); }}
                          options={relacionData}
                          id="relationship"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={relacionSelect}
                        />
                        {campoRequeridoRelacion ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}>
                      <i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "guardar"? t("Save") : t("Edit")}
                    </Button>
                    : null}
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal>
  );
};

ModalEmpresasRelacionadas.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func,
};

export default ModalEmpresasRelacionadas;
