import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation, useHistory } from "react-router-dom"
import {
  Row,
  Col,
  Label,
  Table,
} from "reactstrap"
import { AvForm, AvField } from "availity-reactstrap-validation"
import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper";
import { AttachmentFileInputModel } from '../../../../../models/Common/AttachmentFileInputModel';
import { BackendServices } from "../../../../../services/index.js"
//Import Flatepicker
import "flatpickr/dist/themes/material_blue.css";
import * as url from "../../../../../helpers/url_helper"

const Proyecciones = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  // const { locationData } = props;

  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [formValid, setFormValid] = useState(false);
  const [dataReturn, setDataReturn] = useState(props.dataProyecciones);
  const history = useHistory();
  const [locationData, setLocationData] = useState(null);

  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProyecciones');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  React.useEffect(() => {
    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }

    // Read Api Service data
    initializeData(dataSession);
  }, [props.activeTab == 13]);
  function initializeData(dataLocation) {
    const api = new BackendServices()
    // consultarProyecciones
    api.consultProjections(dataLocation.transactionId).then(resp => {
      setDataReturn(resp);
    })
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    console.log(values);
    setDataReturn(values)
    setFormValid(true)
    //props.onSubmit(values);
  }

  return (
    <React.Fragment>
      <h5>
        {t("Projections")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmProyecciones" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDetails">{t("Details")}</Label>
              <AvField
                className="form-control"
                name="descripcion"
                type="textarea"
                value={props?.dataProyecciones?.descripcion??" "}
                id="descripcion"
                rows="7" />
            </div>
          </Col>
        </Row>
        {locationData ? (props?.activeTab == 13 ?
          <AttachmentFileCore attachmentFileInputModel={new AttachmentFileInputModel(locationData.transactionId, OPTs.PROCESS_INFORMEGESTION, OPTs.ACT_PROYECCIONES)} />
          : null) : null}
        <Row>
          <Col md="12">
            <Label>{t("EstimatesVariationsInItsAssetStructure")}</Label>
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Type")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("Reason")}</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{t("OperatingAssets")}</td>
                  <td>
                    <AvField
                      className="form-control"
                      name="assetsOperatingAmount"
                      type="number"
                      value={props.dataProyecciones.assetsOperatingAmount??0}
                      id="assetsOperatingAmount"
                      validate={{
                        // required: { value: true, errorMessage: t("Required Field") },
                        number: { value: true, errorMessage: t("InvalidField") },
                      }}
                      rows="7" />
                  </td>
                  <td>
                    <AvField
                      className="form-control"
                      name="assetsOperatingReason"
                      type="text"
                      value={props.dataProyecciones.assetsOperatingReason??" "}
                      id="assetsOperatingReason"
                      // validate={{
                      //   required: { value: true, errorMessage: t("Required Field") }
                      // }}
                      rows="7" />
                  </td>
                </tr>
                <tr>
                  <td>{t("FixedAssets")}</td>
                  <td>
                    <AvField
                      className="form-control"
                      name="fixedAssetsAmount"
                      type="number"
                      value={props.dataProyecciones.fixedAssetsAmount??0}
                      id="fixedAssetsAmount"
                      validate={{
                        // required: { value: true, errorMessage: t("Required Field") },
                        number: { value: true, errorMessage: t("InvalidField") },
                      }}
                      rows="7" />
                  </td>
                  <td>
                    <AvField
                      className="form-control"
                      name="fixedAssetsReason"
                      type="text"
                      value={props.dataProyecciones.fixedAssetsReason??" "}
                      id="fixedAssetsReason"
                      // validate={{
                      //   required: { value: true, errorMessage: t("Required Field") }
                      // }}
                      rows="7" />
                  </td>
                </tr>
                <tr>
                  <td>{t("OthersAssets")}</td>
                  <td>
                    <AvField
                      className="form-control"
                      name="othersAssetsAmount"
                      type="number"
                      value={props.dataProyecciones.othersAssetsAmount??0}
                      id="othersAssetsAmount"
                      validate={{
                        // required: { value: true, errorMessage: t("Required Field") },
                        number: { value: true, errorMessage: t("InvalidField") },
                      }}
                      rows="7" />
                  </td>
                  <td>
                    <AvField
                      className="form-control"
                      name="othersAssetsReason"
                      type="text"
                      value={props.dataProyecciones.othersAssetsReason??" "}
                      id="othersAssetsReason"
                      // validate={{
                      //   required: { value: true, errorMessage: t("Required Field") }
                      // }}
                      rows="7" />
                  </td>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Label>{t("EstimatesVariationsInItsFinancingSources")}</Label>
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Type")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("Reason")}</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{t("Banking")}</td>
                  <td>
                    <AvField
                      className="form-control"
                      name="bankAmount"
                      type="number"
                      value={props.dataProyecciones.bankAmount??0}
                      id="bankAmount"
                      validate={{
                        // required: { value: true, errorMessage: t("Required Field") },
                        number: { value: true, errorMessage: t("InvalidField") },
                      }}
                      rows="7" />
                  </td>
                  <td>
                    <AvField
                      className="form-control"
                      name="bankReason"
                      type="text"
                      value={props.dataProyecciones.bankReason??" "}
                      id="bankReason"
                      // validate={{
                      //   required: { value: true, errorMessage: t("Required Field") }
                      // }}
                      rows="7" />
                  </td>
                </tr>
                <tr>
                  <td>{t("Providers")}</td>
                  <td>
                    <AvField
                      className="form-control"
                      name="providersAmount"
                      type="number"
                      value={props.dataProyecciones.providersAmount??0}
                      id="providersAmount"
                      validate={{
                        // required: { value: true, errorMessage: t("Required Field") },
                        number: { value: true, errorMessage: t("InvalidField") },
                      }}
                      rows="7" />
                  </td>
                  <td>
                    <AvField
                      className="form-control"
                      name="providersReason"
                      type="text"
                      value={props.dataProyecciones.providersReason??" "}
                      id="providersReason"
                      // validate={{
                      //   required: { value: true, errorMessage: t("Required Field") }
                      // }}
                      rows="7" />
                  </td>
                </tr>
                <tr>
                  <td>{t("Capital")}</td>
                  <td>
                    <AvField
                      className="form-control"
                      name="capitalAmount"
                      type="number"
                      value={props.dataProyecciones.capitalAmount??0}
                      id="capitalAmount"
                      validate={{
                        // required: { value: true, errorMessage: t("Required Field") },
                        number: { value: true, errorMessage: t("InvalidField") },
                      }}
                      rows="7" />
                  </td>
                  <td>
                    <AvField
                      className="form-control"
                      name="capitalReason"
                      type="text"
                      value={props.dataProyecciones.capitalReason??" "}
                      id="capitalReason"
                      // validate={{
                      //   required: { value: true, errorMessage: t("Required Field") }
                      // }}
                      rows="7" />
                  </td>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>

        <Row>
          <Col md="6">
            <div className="mb-3">
              <Label>{t("MaximumAmountOfBankDebtToTake")}</Label>
              <AvField
                className="form-control"
                name="maximodeuda"
                type="text"
                value={props.dataProyecciones.maximodeuda??0}
                id="maximodeuda"
                validate={{
                  // required: { value: true, errorMessage: t("Required Field") },
                  number: { value: true, errorMessage: t("InvalidField") },
                }} />
            </div>
          </Col>
          <Col md="6">
            <div className="mb-3">
              <Label>{t("EstimatedDate")}</Label>
              <AvField
                className="form-control"
                name="estimatedDate"
                type="date"
                value={props.dataProyecciones.estimatedDate}
                id="estimatedDate"
              />
            </div>
          </Col>
        </Row>
      </AvForm>
    </React.Fragment>
  );
});
Proyecciones.propTypes = {
  onSubmit: PropTypes.func.isRequired,
  locationData: PropTypes.any,
}
export default Proyecciones;
