/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types'
import { useHistory, useLocation } from 'react-router-dom'

import { Link } from "react-router-dom"

import {
  Card,
  CardBody,
  Col,
  Button,
  CardFooter,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../../../components/Common/Breadcrumb"

//i18n
import { withTranslation } from "react-i18next"

import DatosGenerales from "./DatosGenerales"
import SweetAlert from "react-bootstrap-sweetalert"
import { BpmServices } from "../../../../../services"
import * as url from "../../../../../helpers/url_helper";

import { BackendServices, CoreServices, } from "../../../../../services";

import { AvForm, AvField } from "availity-reactstrap-validation"
import { useTranslation } from "react-i18next"

const PantallaBusqueda = props => {
  const { t, i18n } = useTranslation();

  const apiServiceBackend = new BackendServices();
  const apiServiceCore = new CoreServices();


  const history = useHistory();

  const location = useLocation()
  const { selectedData } = location;

  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");


  const [dataLocation, setData] = useState(location.data);
  const [DataGeneral, setDataGeneral] = useState(null);
  const [DataInstructivo, setDataInstructivo] = useState(null);
  const [PaisesCatalogo, setPaisesCatalogo] = useState(null);
  const [ProvinceCatalogo, setProvinceCatalogo] = useState(null);
  const [ProductosCatalogo, setProductosCatalogo] = useState(null);
  const [SubcursalCatalogo, setSubcursalCatalogo] = useState(null);
  const [economicActivity, seteconomicActivity] = useState(null);
  const [subeconomicActivity, setsubeconomicActivity] = useState(null);
  const [PrestamoCatalogo, setPrestamoCatalogo] = useState(null);

  React.useEffect(() => {
    LoadDataDataGeneral();
    LoadDataDataInstructivoD();
    // LoadDataPais();
  }, []);

  async function onSaveProcess(option, values = null) {
    switch (option) {
      case 1: {

        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 3: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 4: {

        let newDatos = {
          branch: SubcursalCatalogo,
          subproduct: ProductosCatalogo,
          destinyCountry: PaisesCatalogo,
          province: ProvinceCatalogo,
          ...values
        }

        await apiServiceBackend.guardarDatosGeneralesDesembolso(newDatos);

        if (DataInstructivo != null || DataInstructivo != undefined) {
          await apiServiceBackend.actualizarInstructivoDesembolso(newDatos);
        } else {
          await apiServiceBackend.nuevoInstructivoDesembolso(newDatos);
        }



        let descripcion = 0;
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        api.abortProcess(descripcion).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
    }

  }


  function LoadDataDataGeneral() {
    apiServiceBackend.consultarDatosGeneralesDesembolso(dataLocation.transactionId).then(data => {
      if (data != null || data != undefined) {
        setSubcursalCatalogo(data.branch);
        setProductosCatalogo(data.subproduct);
        setDataGeneral(data)
      };
    });
  }

  function LoadDataDataInstructivoD() {
    apiServiceBackend.consultarInstructivoDesembolso(dataLocation.transactionId).then(data => {
      if (data != null || data != undefined) {
        setProvinceCatalogo(data.province);
        setPaisesCatalogo(data.destinyCountry);
        setsubeconomicActivity(data.cinuActivity);
        /**
         * falta set de actividad
        */
        setDataInstructivo(data);
      }
    });
  }

  async function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    console.log(values);
    onSaveProcess(4, values);

    //props.onSubmiprops.t(values);
  }



  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("DisbursementInstructions")} />

        <Card>
          <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>

            <CardBody>

              <DatosGenerales
                DataGeneral={DataGeneral}
                DataInstructivo={DataInstructivo}
                PaisesCatalogo={PaisesCatalogo}
                setPaisesCatalogo={setPaisesCatalogo}
                ProvinceCatalogo={ProvinceCatalogo}
                setProvinceCatalogo={setProvinceCatalogo}
                ProductosCatalogo={ProductosCatalogo}
                setProductosCatalogo={setProductosCatalogo}
                SubcursalCatalogo={SubcursalCatalogo}
                setSubcursalCatalogo={setSubcursalCatalogo}
                economicActivity={economicActivity}
                seteconomicActivity={seteconomicActivity}
                subeconomicActivity={subeconomicActivity}
                setsubeconomicActivity={setsubeconomicActivity}
                PrestamoCatalogo={PrestamoCatalogo}
                setPrestamoCatalogo={setPrestamoCatalogo}
              />

            </CardBody>
            <CardFooter style={{ textAlign: "right" }}>

              <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {props.t("Exit")}</Button>

              <Button color="negative" type="button" onClick={() => { onSaveProcess(3); }} style={{ margin: '5px' }}>
                <i className="mdi mdi-block-helper mdi-12px"></i>{" "}{props.t("Refuse")}
              </Button>

              <Button color="success" type="submit" style={{ margin: '5px' }}>
                <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {props.t("Advance")}
              </Button>
              {/* <Button color="success" type="button" onClick={() => { onSaveProcess(1); }} style={{ margin: '5px' }}>
              <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {props.t("GenerateDocument")}
            </Button> */}
            </CardFooter>
          </AvForm>


        </Card>
        {successSave_dlg ? (
          <SweetAlert
            success
            title={props.t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)

              history.push(url.URL_DASHBOARD);
            }}
          >
            {props.t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={props.t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
      </div>

    </React.Fragment>
  )
}

PantallaBusqueda.propTypes = {
  selectedId: PropTypes.any,
  onClose: PropTypes.func.isRequired
}

export default (withTranslation()(PantallaBusqueda))
