                    <Label htmlFor="detail">{t("DetailWidely")}</Label>                            {/**No Item */}
                    <AvField
                      className="form-control"
                      name="detail"
                      type="textarea" rows={7}
                      id="detail"
                      value={DataGeneral?.detail ?? ''} />
                  </div>
                </Col>
              </Row>
              {/* Next seccion */}
              <Row>
                <Col md="12">
                  <span className="mb-3" style={S_seccions}>{t("Disbursement Instructions")}</span>
                </Col>
              </Row>

              <Row>
                <Col md="4">
                  <div className="mb-3">
                    <div class="kt-checkbox-list">
                      <label class="kt-checkbox d-flex flex-row justify-content-between align-items-center">
                        <span>{t("Crdito a Cuenta")}</span>
                        <AvGroup check className="">
                          <Switch name="preapproval"
                            uncheckedIcon={<Offsymbol />}
                            checkedIcon={<OnSymbol />}
                            onColor="#007943"
                            className="form-label"
                            onChange={(e) => { setcreditAccount(!creditAccount) }}
                            checked={creditAccount}
                          />
                        </AvGroup>
                      </label>
                      <label class="kt-checkbox d-flex flex-row justify-content-between align-items-center">
                        <span>{t("Cheque de gerencia")}</span>
                        <AvGroup check className="">
                          <Switch name="preapproval"
                            uncheckedIcon={<Offsymbol />}
                            checkedIcon={<OnSymbol />}
                            onColor="#007943"
                            className="form-label"
                            onChange={(e) => { setchecker(!checker) }}
                            checked={checker}
                          />
                        </AvGroup>
                      </label>
                      <label class="kt-checkbox d-flex flex-row justify-content-between align-items-center">
                        <span>{t("Transferencia")}</span>
                        <AvGroup check className="">
                          <Switch name="preapproval"
                            uncheckedIcon={<Offsymbol />}
                            checkedIcon={<OnSymbol />}
                            onColor="#007943"
                            className="form-label"
                            onChange={(e) => { settransfer(!transfer) }}
                            checked={transfer}
                          />
                        </AvGroup>
                      </label>
                      <label class="kt-checkbox d-flex flex-row justify-content-between align-items-baseline mt-4">
                        <div className="d-flex flex-row align-items-baseline">
                          <span style={{ marginRight: "10px" }}>{t("Otro")}</span>
                        </div>
                        <AvGroup check className="">
                          <Switch name="preapproval"
                            uncheckedIcon={<Offsymbol />}
                            checkedIcon={<OnSymbol />}
                            onColor="#007943"
                            className="form-label"
                            onChange={(e) => { setothers(!others) }}
                            checked={others}
                          />
                        </AvGroup>
                      </label>
                    </div>
                  </div>
                </Col>
              </Row>

              {creditAccount && (<Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="creditAccountNumber">{t("Credit to account Number")}</Label>
                    <AvField
                      className="form-control"
                      name="creditAccountNumber"
                      type="text"
                      id="creditAccountNumber"
                      value={DataGeneral?.creditAccountNumber ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="accountName">{t("Account name")}</Label>
                    <AvField
                      className="form-control"
                      name="accountName"
                      type="text"
                      id="accountName"
                      value={DataGeneral?.accountName ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="creditAccountAmount">{t("Amount")}</Label>
                    <AvField
                      className="form-control"
                      name="creditAccountAmount"
                      type="number"
                      id="creditAccountAmount"
                      onChange={(e) => { sumdisbursementTotalAmount(); }}
                      onKeyUp={(e) => { setcreditAccountAmount(e.target.value); }}
                      value={DataGeneral?.creditAccountAmount ?? ''} />
                  </div>
                </Col>
              </Row>)}

              {checker && (<Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="checkNumber">{t("Cashier's Check No")}</Label>
                    <AvField
                      className="form-control"
                      name="checkNumber"
                      type="text"
                      id="checkNumber"
                      value={DataGeneral?.checkNumber ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="beneficiary">{t("Beneficiary")}</Label>
                    <AvField
                      className="form-control"
                      name="beneficiary"
                      type="text"
                      id="beneficiary"
                      value={DataGeneral?.beneficiary ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="checkAmount">{t("Amount")}</Label>
                    <AvField
                      className="form-control"
                      name="checkAmount"
                      type="number"
                      id="checkAmount"
                      onKeyUp={(e) => { setcheckAmount(e.target.value); }}
                      onChange={(e) => { sumdisbursementTotalAmount(); }}
                      value={DataGeneral?.checkAmount ?? ''} />
                  </div>
                </Col>
              </Row>)}

              {transfer && (<Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="transferenceNumber">{t("No Transferencia")}</Label>
                    <AvField
                      className="form-control"
                      name="transferenceNumber"
                      type="text"
                      id="transferenceNumber"
                      value={DataGeneral?.transferenceNumber ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="transferenceBeneficiary">{t("Beneficiary")}</Label>
                    <AvField
                      className="form-control"
                      name="transferenceBeneficiary"
                      type="text"
                      id="transferenceBeneficiary"
                      value={DataGeneral?.transferenceBeneficiary ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="transferAmount">{t("Amount")}</Label>
                    <AvField
                      className="form-control"
                      name="transferAmount"
                      type="number"
                      id="transferAmount"
                      onKeyUp={(e) => { settransferAmount(e.target.value); }}
                      onChange={(e) => { sumdisbursementTotalAmount(); }}
                      value={DataGeneral?.transferAmount ?? ''} />
                  </div>
                </Col>
              </Row>)}

              {others && (<Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="othersItem">{t("Others")}</Label>
                    <AvField
                      className="form-control"
                      name="othersItem"
                      type="text"
                      id="othersItem"
                      value={DataGeneral?.othersItem ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="othersDescription">{t("Description")}</Label>
                    <AvField
                      className="form-control"
                      name="othersDescription"
                      type="text"
                      id="othersDescription"
                      value={DataGeneral?.othersDescription ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="othersAmount">{t("Amount")}</Label>
                    <AvField
                      className="form-control"
                      name="othersAmount"
                      type="number"
                      id="othersAmount"
                      onKeyUp={(e) => { setothersAmount(e.target.value); }}
                      onChange={(e) => { sumdisbursementTotalAmount(); }}
                      value={DataGeneral?.othersAmount ?? ''} />
                  </div>
                </Col>
              </Row>)}

              <Row>
                <Col md="12" className="d-flex flex-row justify-content-between">
                  <Col md="3">
                    <div className="mb-3">
                      <Label htmlFor="cppNumber">{t("No. CPP")}</Label>
                      <AvField
                        className="form-control"
                        name="cppNumber"
                        type="text"
                        id="cppNumber"
                        value={DataGeneral?.cppNumber ?? ''} />
                    </div>
                  </Col>
                  <Col md="3">
                    <div className="mb-3">
                      <Label htmlFor="disbursementTotalAmount">{t("Total amount")}</Label>
                      <AvField
                        className="form-control"
                        name="disbursementTotalAmount"
                        type="number"
                        disabled={true}
                        id="disbursementTotalAmount"
                        value={disbursementTotalAmount} />
                    </div>
                  </Col>
                </Col>
              </Row>

              <Row>
                <Col md="12">
                  <div className="mb-3">
                    <Label htmlFor="specialInstructions">{t("Bank special instructions")}</Label>                            {/**No Item */}
                    <AvField
                      className="form-control"
                      name="specialInstructions"
                      type="textarea" rows={7}
                      id="specialInstructions"
                      value={DataGeneral?.specialInstructions ?? ''} />
                  </div>
                </Col>
              </Row>

              <Row>
                <Col md="12">
                  <span className="mb-3" style={S_seccions}>{t("Operating Instructions")}</span>
                </Col>
              </Row>

              <Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="invoiceIop">{t("Lawyer Bill")}</Label>
                    <AvField
                      className="form-control"
                      name="invoiceIop"
                      type="text"
                      id="invoiceIop"
                      value={DataGeneral?.invoiceIop ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="checkIop">{t("Management check")}</Label>
                    <AvField
                      className="form-control"
                      name="checkIop"
                      type="text"
                      id="checkIop"
                      value={DataGeneral?.checkIop ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="accountNumberIop">{t("Cr Account No.")}</Label>
                    <AvField
                      className="form-control"
                      name="accountNumberIop"
                      type="text"
                      id="accountNumberIop"
                      value={DataGeneral?.accountNumberIop ?? ''} />
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="2">
                  <div className="mb-3">
                    <Label htmlFor="commissionIop">{t("Commission")}</Label>
                    <AvField
                      className="form-control"
                      name="commissionIop"
                      type="text"
                      id="commissionIop"
                      value={DataGeneral?.commissionIop ?? ''} />
                  </div>
                </Col>
                <Col md="2">
                  <div className="mb-3">
                    <Label htmlFor="commissionAmountIop">{t("Commission amount")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="commissionAmountIop"
                      type="number"
                      id="commissionAmountIop"
                      value={DataGeneral?.commissionAmountIop ?? ''} />
                  </div>
                </Col>

                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="itbmsIop">{t("ITBM")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="itbmsIop"
                      type="text"
                      id="itbmsIop"
                      value={DataGeneral?.itbmsIop ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="field59">{t("To differ")}</Label>
                    {DataGeneral?.toDifferIop != null ? <Select
                      showSearch
                      style={{ width: "100%" }}
                      placeholder={t("SearchtoSelect")}
                      optionFilterProp="children"
                      defaultValue={DataGeneral?.toDifferIop}
                      onChange={(e) => { settoDifferIop(e) }}
                      filterOption={(input, option) =>
                        option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      {toDifferIopCatalogue?.length > 0 ?

                        toDifferIopCatalogue?.map((item, index) => (
                          <Option key={index} value={item.value}>{item.label}</Option>
                        ))
                        : null}
                    </Select> : null}
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="field59">{t("Pledge")}</Label>
                    {DataGeneral?.pawnIop != null ? <Select
                      showSearch
                      style={{ width: "100%" }}
                      placeholder={t("SearchtoSelect")}
                      optionFilterProp="children"
                      defaultValue={DataGeneral?.pawnIop}
                      onChange={(e) => { setpawnIop(e) }}
                      filterOption={(input, option) =>
                        option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      {pawnIopCatalogue?.length > 0 ?

                        pawnIopCatalogue?.map((item, index) => (
                          <Option key={index} value={item.value}>{item.label}</Option>
                        ))
                        : null}
                    </Select> : null}
                  </div>
                </Col>

                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="dpfSavingsIop">{t("DPF/Saving No(ID number in system)")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="dpfSavingsIop"
                      type="text"
                      id="dpfSavingsIop"
                      value={DataGeneral?.dpfSavingsIop ?? ''} />
                  </div>
                </Col>

                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="anIop">{t("A/N")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="anIop"
                      type="text"
                      id="anIop"
                      value={DataGeneral?.anIop ?? ''} />
                  </div>
                </Col>

              </Row>
              <Row>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="pawnAmountIop">{t("Amount to pledge")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="pawnAmountIop"
                      type="number"
                      id="pawnAmountIop"
                      value={DataGeneral?.pawnAmountIop ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="notaryIop">{t("Notary")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="notaryIop"
                      type="text"
                      id="notaryIop"
                      value={DataGeneral?.notaryIop ?? ''} />
                  </div>
                </Col>
                <Col md="4">
                  <div className="mb-3">
                    <Label htmlFor="stampsIop">{t("Stamps")}</Label>                     {/*No Item*/}
                    <AvField
                      className="form-control"
                      name="stampsIop"
                      type="text"
                      id="stampsIop"
                      value={DataGeneral?.stampsIop ?? ''} />
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="12">
                  <div className="mb-3">
                    <Label htmlFor="instructionsIop">{t("Credit Admin Analyst Instructions (Review)")}</Label>                            {/**No Item */}
                    <AvField
                      className="form-control"
                      name="instructionsIop"
                      type="textarea" rows={7}
                      id="instructionsIop"
                      value={DataGeneral?.instructionsIop ?? ''} />
                  </div>
                </Col>
              </Row>

            </Col>
          </Row >

        </LoadingOverlay >

        <CardFooter style={{ textAlign: "right" }}>

          <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { props.onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {t("Exit")}</Button>

          <Button color="negative" type="button" onClick={() => { props.onSaveProcess(3); }} style={{ margin: '5px' }}>
            <i className="mdi mdi-block-helper mdi-12px"></i>{" "}{t("Refuse")}
          </Button>

          <Button color="success" type="submit" style={{ margin: '5px' }}>
            <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {t("Advance")}
          </Button>
          {/* <Button color="success" type="button" onClick={() => { props.onSaveProcess(1); }} style={{ margin: '5px' }}>
              <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {t("GenerateDocument")}
            </Button> */}
        </CardFooter>
      </AvForm>
    </React.Fragment >


  );

}

export default (withTranslation()(DatosGenerales))
