import PropTypes from 'prop-types';
import { formatCurrency, translationHelpers } from '../../../../helpers';

import {
  Table,
  Card,
  CardBody,
} from "reactstrap"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import React, { useEffect, useState } from "react"
import { useTranslation } from 'react-i18next';


const ExposicionCorportativa = (props) => {
  const coreServices = new CoreServices();

  const { t, i18n } = useTranslation();
  const [dataRows, setdataRows] = React.useState(null);
  const [ExposicionCorportativa, setExposicionCorportativa] = useState(null);

  // const customerNumberT24 = props.prospect?.customerNumberT24;
  const customerNumberT24 = "600133774";
  // React.useEffect(() => {
  //   setdataRows(props.items.map((item, index) => (
  //     <tr key={'row-' + index}>
  //       <th scope="row" width="30%">{item.name}</th>
  //       <td className="text-end">{formatCurrency('$', item.approvalRisk, 0)}</td>
  //       <td className="text-end">{formatCurrency('$', item.proposalRisk, 0)}</td>
  //       <td className="text-end">{formatCurrency('$', item.variationRisk, 0)}</td>
  //       <td className="text-end">0%</td>
  //     </tr>)
  //   ));
  // }, [props]);

  useEffect(() => {
    getCoporateExhibition();
  }, []);


  function getCoporateExhibition() {
    coreServices.getCorporateExhibition(customerNumberT24).then(resp => {
      console.log(resp);
      setExposicionCorportativa(resp);
      setdataRows(resp.map((item, index) => (
        <tr key={'row-' + index}>
          <th scope="row" width="30%">{item.name}</th>
          <th scope="row" className="table-right">{formatCurrency('$', item.approvalRisk, 0)}</th>
          <th scope="row" className="table-right">{formatCurrency('$', item.variationRisk, 0)}</th>
          <th scope="row" className="table-right">{formatCurrency('$', item.proposalRisk, 0)}</th>
        </tr>
      )
      ));
    });
  }

  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <h5>{props.title}</h5>
          <p className="card-title-desc">
          </p>
          <div className="table-responsive">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th></th>
                  <th className="table-right">{t("ApprovedRisk")}</th>
                  <th className="table-right">{t("ProposedRisk")}</th>
                  <th className="table-right">{t("Variation")}</th>
                </tr>
              </thead>
              <tbody>
                {dataRows}
                <tr>
                  <th scope="row" width="30%">{t("Total")}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => (acu.approvalRisk ?? acu) + crr.approvalRisk), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => ((acu.variationRisk ?? acu) + crr.variationRisk)), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', ExposicionCorportativa?.reduce((acu, crr) => ((acu.proposalRisk ?? acu) + crr.proposalRisk)), 0)}</th>
                </tr>
              </tbody>
            </Table>
          </div>
        </CardBody>
      </Card>
    </React.Fragment >
  );

};

ExposicionCorportativa.propTypes = {
  title: PropTypes.string,
  items: PropTypes.array.isRequired
};


export default ExposicionCorportativa;
