it")]}),/*#__PURE__*/_jsxs(Button,{color:"success",type:"button",style:{margin:'5px'},onClick:function onClick(){onSaveProcess(OPTs.PROCESS_ANALISISCREDITO);},children:[/*#__PURE__*/_jsx("i",{className:"mdi mdi-arrow-right-bold-circle-outline mid-12px"})," ",t("Continue")]})]})]})]})]}),locationData?/*#__PURE__*/_jsx(LogProcess,{logProcessModel:new LogProcessModel(locationData.instanceId,locationData.transactionId,0,"","",OPTs.PROCESS_PROPUESTACREDITO,OPTs.ACT_NONE)}):null,successSave_dlg?/*#__PURE__*/_jsx(SweetAlert,{success:true,title:t("SuccessDialog"),onConfirm:function onConfirm(){setsuccessSave_dlg(false);},children:t("SuccessSaveMessage")}):null,error_dlg?/*#__PURE__*/_jsx(SweetAlert,{error:true,title:t("ErrorDialog"),onConfirm:function onConfirm(){seterror_dlg(false);},children:error_msg}):null]}),loading&&/*#__PURE__*/_jsx(LoadingIndicator,{message:t("Please Wait")}),showSuccess&&/*#__PURE__*/_jsx(SuccessMessage,{title:t("Operation Complete"),message:t("Credit Proposal Saved")}),/*#__PURE__*/_jsx(ModalPrevisualizarPropCred,{isOpen:ModalPrevisualizardata,toggle:function toggle(){toogleModalPrevisualizar();}}),/*#__PURE__*/_jsx(ModalCloseOptions,{onSaveProcess:onSaveProcess,isOpen:displayModalCloseOptions,toggle:function toggle(){toggleModalCloseOptions();}}),/*#__PURE__*/_jsx(ModalEndProcess,{onSaveEndProcess:onSaveEndProcess,isOpen:displayModalEndProcess,toggle:function toggle(){showModalEndProcess(false);}})]}):null});};export default CreditProposalPage;

