import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useTranslation } from 'react-i18next'
import Select from "react-select";
import { useLocation, useHistory } from 'react-router-dom'
import LoadingOverlay from 'react-loading-overlay';

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Table,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"

import { CoreServices, BackendServices } from '../../services';
import Currency from '../../helpers/currency'; import { uniq_key } from '../../helpers/unq_key';
;

const AccountMovementsHistory = (props) => {
  const currencyData = new Currency();
  const { t, i18n } = useTranslation();

  const [dataRows, setDataRows] = useState([]);
  const [isActiveLoading, setIsActiveLoading] = useState(false);

  const [apiServiceBackend, setapiServiceBackend] = useState(new BackendServices());
  const [apiCoreServices, setCoreServices] = useState(new CoreServices());

  const [dataMovimientos, setdataMovimientos] = useState(null);
  //On Mounting (componentDidMount)
  React.useEffect(() => {
    fetchData();
  }, []);

  function fetchData() {
    loadData();
  }
  function groupBy(collection, property) {
    var i = 0, val, index,
      values = [], result = [];
    for (; i < collection.length; i++) {
      val = collection[i][property];
      index = values.indexOf(val);
      if (index > -1)
        result[index].push(collection[i]);
      else {
        values.push(val);
        result.push([collection[i]]);
      }
    }
    return result;
  }
  //cargar lista de facilidades
  async function loadData() {
    await apiServiceBackend.consultMovementsBank(props?.transactionId ?? 0).then(resp => {
      if (resp.bankBanesco.length > 0) {
        //toca dibujar la tabla
        if (resp.bankBanesco.length > 0) {
          resp = resp.bankBanesco;
          // console.log("ssdadasda",groupBy(resp,"accountNumber"));
          // let newJson=groupBy(resp,"accountNumber");
          setdataMovimientos(resp.map((data, index, arr) => {
            let movementsYear = resp?.filter(movement => movement.year === data.year && movement.status&&movement.accountNumber === data.accountNumber);
            return (
              data.status ?
                <>
                  <tr key={uniq_key() + "1"}>
                    <th>{arr[index - 1]?.accountNumber!=data.accountNumber&&data.accountNumber}</th>
                    <td>{data.year}</td>
                    <td>{data.month}</td>
                    <td>${currencyData.formatTable(data.averageBalance ?? 0)}</td>
                    <td>${currencyData.formatTable(data.deposits ?? 0)}</td>

                  </tr>
                  {data.year !== arr[index + 1]?.year && (
                    <>
                      <tr key={uniq_key() + "2"}>
                        <td></td>
                        <th><b>{t("Total")}</b></th>
                        <td></td>
                        <th>${currencyData.formatTable(movementsYear?.reduce((acu, crr) => acu + crr.averageBalance, 0).toFixed(2) ?? 0)}</th>
                        <th>${currencyData.formatTable(movementsYear?.reduce((acu, crr) => acu + crr.deposits, 0).toFixed(2) ?? 0)}</th>
                        <td></td>
                      </tr>
                      <tr key={uniq_key() + "3"}>
                        <td></td>
                        <th><b>{t("Average")}</b></th>
                        <td></td>
                        <th>${currencyData.formatTable(movementsYear?.reduce((acu, crr) => acu + crr.averageBalance / movementsYear.length, 0).toFixed(2) ?? 0)}</th>
                        <th>${currencyData.formatTable(movementsYear?.reduce((acu, crr) => acu + crr.deposits / movementsYear.length, 0).toFixed(2) ?? 0)}</th>
                        <td></td>
                      </tr>
                    </>
                  )}
                </>
                : null)
          }
          ));
        } else {
          setdataMovimientos(
            <tr key={uniq_key() + "2"}>
              <td colSpan="6" style={{ textAlign: 'center' }}>{t("NoData")}</td>
            </tr>);
        }
      } else {
        //guardamos los datos de t24
        apiCoreServices.getAccountMovements(props.customerNumberT24)
          .then(async (data) => {
            console.log("getAccountMovements", data);
            if (data !== null && data !== undefined) {
              for (let i = 0; i < data.length; i++) {
                //aqui cargamos por cuentas
                let cuenta = data[i].AcctId;
                for (let j = 0; j < data[i].dataByYear.length; j++) {
                  console.log("data[i].dataByYear",data[i].dataByYear);
                  for (let k = 0; k < data[i].dataByYear[j].dataresult.length; k++) {
                    let jsonSet = {
                      "trasactId": Number(props?.transactionId),
                      "year": data[i].dataByYear[j].dataresult[k].year,
                      "month":  data[i].dataByYear[j].dataresult[k].month,
                      "deposits":  data[i].dataByYear[j].dataresult[k].deposits,
                      "averageBalance":  data[i].dataByYear[j].dataresult[k].amount,
                      "observations": "",
                      "accountNumber": cuenta,
                      "t24": true
                    }
                    await apiServiceBackend.newMovementsAccountsIGR(jsonSet).then(resp => {
                      
                    })
                    
                  }

                }

              }
              loadData()
              
            }
          }).catch((error) => { });
      }

    });


    // apiCoreServices.getAccountMovements(props.customerNumberT24)
    //   .then((data) => {
    //     console.log("getAccountMovements", data);
    //     if (data !== null && data !== undefined) {
    //       var rows = data.map((dataRow, index) => (
    //         dataRow.dataByYear.map((dataByYear, index2) => (
    //           <>
    //             {dataByYear.dataresult.map((dataresult, index3) => (
    //               <tr key={index2 + index3}>
    //                 <th>{index2 === 0 && index3 === 0 ? dataRow.AcctId : ""}</th>
    //                 <td>{dataByYear.year}</td>
    //                 <td>{t(dataresult.month)}</td>
    //                 <td>${currencyData.formatTable((Number(dataresult?.amount ?? 0)).toFixed(2))}</td>
    //                 {<td>${currencyData.formatTable((Number(dataresult?.deposits ?? 0)).toFixed(2))}</td>}
    //               </tr>
    //             ))}
    //             <tr>
    //               <th></th>
    //               <th>{t("Total")}</th>
    //               <th></th>
    //               <th>${currencyData.formatTable((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.amount), 0)).toFixed(2))}</th>
    //               <th>${currencyData.formatTable((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.deposits), 0)).toFixed(2))}</th>
    //             </tr>
    //             <tr>
    //               <th></th>
    //               <th>{t("Average")}</th>
    //               <th></th>
    //               <th>${currencyData.formatTable((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.amount), 0) / dataByYear.dataresult.length).toFixed(2))}</th>
    //               <th>${currencyData.formatTable((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.deposits), 0) / dataByYear.dataresult.length).toFixed(2))}</th>
    //             </tr>
    //           </>
    //         ))
    //       )
    //       );
    //       setDataRows(rows);
    //     }
    //   }).catch((error) => { });
  }

  return (

    <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>

      <Card>

        <Row>
          <Col md="6">
            <h5 className="card-sub-title">{t("AccountMovementsHistory") + " - Banesco"}</h5>
          </Col>
        </Row>

        <CardBody>

          <Row>
            <Col md="12" className="table-responsive styled-table-div">
              <Table className="table table-striped table-hover styled-table table">
                <thead>
                  <tr>
                    <th>{t("Account")}</th>
                    <th>{t("Year")}</th>
                    <th>{t("Month")}</th>
                    <th>{t("AverageAmount")}</th>
                    <th>{t("Deposits")}</th>
                  </tr>
                </thead>
                <tbody>
                  {/* {dataRows.length > 0 ? dataRows : null} */}
                  {dataMovimientos}
                </tbody>
              </Table>
            </Col>
          </Row>
        </CardBody>
      </Card>

    </LoadingOverlay>

  )
}

AccountMovementsHistory.propTypes = {
  customerNumberT24: PropTypes.string
}

//export default (withTranslation()(DatosGenerales));
export default AccountMovementsHistory;
