import { useState } from 'react';
import PropTypes from 'prop-types';
import { translationHelper } from '../../helpers';

import {
  Table,
  Card,
  CardBody,
  Row,
  Col,
  Label,
  Button
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import ModalReportingServices from './ModalReportingServices';


const FormCreditRisk = (props) => {

  const t = translationHelper('financial_report');

  const [displayModalReportingServices, setDisplayModalReportingServices] = useState(false);

  function toggleReportingServices() {
    setDisplayModalReportingServices(!displayModalReportingServices);
  }

  return (
    <>
      <Card>
        <CardBody>
          <h4 className="card-title">{t("CreditRiskOpinion")}
            <Button id="btnReportingServices" color="success" type="button"
              className="float-end" style={{ margin: '5px' }}
              onClick={ toggleReportingServices }>
              {t("Reporting Services")}
            </Button>
            <Button id="btnAskOpinion" color="success" type="button" className="float-end" style={{ margin: '5px' }}>
              {t("AskOpinion")}
            </Button>
          </h4>
          <p className="card-title-desc"> &emsp;</p>
          <Row>
            <Col md="12">
              <AvGroup className="mb-3">
                <AvField
                  className="form-control"
                  name="opinion"
                  type="textarea"
                  id="opinion"
                  value={props.Opinion ?? ''}
                  rows="8" />
              </AvGroup>
            </Col>
          </Row>
        </CardBody>
      </Card>
      <ModalReportingServices isOpen={displayModalReportingServices} toggle={toggleReportingServices} />
    </>
  );
};

FormCreditRisk.propTypes = {
};

FormCreditRisk.defaultProps = {
};

export default FormCreditRisk;
