import PropTypes from 'prop-types';
import { withTranslation } from "react-i18next"
import { Link } from "react-router-dom"
import React, { useState } from "react"
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import {
  Table,
  Card,
  CardBody,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
  Button,
  Row,
  Col,
} from "reactstrap"


const DatosServicioFiduciario = (props) => {
  React.useEffect(() => {
    // SumTotal();
    if (props.ServiciosFiduciario?.guaranteetrust) {
      SumTotal();
    }
  }, [props.ServiciosFiduciario]);
  const results = [
    // { id: 1, comentario: "Fideicomiso de Garantia" },
    // { id: 2, comentario: "Fideicomiso de  Administracion" },
    // { id: 3, comentario: "Fideicomiso de  Inversión " },
    // { id: 4, comentario: "Fideicomiso patrimonial y Testamento" },
    // { id: 5, comentario: "Escrow account (Deposito en Plica)" },
    // { id: 6, comentario: "Otros especifique" },
    // { id: 7, comentario: "TOTAL " }
  ];

  const [Total, setTotal] = useState(0);
  const [guaranteetrust, setguaranteetrust] = useState(0);
  const [administrationTrust, setadministrationTrust] = useState(0);
  const [investmentTrust, setinvestmentTrust] = useState(0);
  const [stateTrust, setstateTrust] = useState(0);
  const [scrowAccount, setscrowAccount] = useState(0);
  const [others, setothers] = useState(0);

  // const dataRows = results.map((data) => (
  // <tr key={data.id}>
  //   <td>{data.comentario}</td>
  //   {data.comentario === "Otros especifique" && (
  //     <th scope="row">
  //       <input className="form-control" type="text" />
  //     </th>
  //   )}
  //   {data.comentario !== "Otros especifique" &&
  //     (<td></td>)}
  //   <th scope="row">
  //     <input className="form-control" type="text" />
  //   </th>
  // </tr>)
  // );



  function SumTotal() {
    setTotal((+document.getElementById('guaranteetrust').value) + (+document.getElementById('administrationTrust').value) + (+document.getElementById('investmentTrust').value) + (+document.getElementById('stateTrust').value) + (+document.getElementById('scrowAccount').value) + (+document.getElementById('others').value))
  }

  return (
    <React.Fragment>

      <CardBody>
        <Row>
          <Col lg="12">
            <div className="table-responsive">
              <Table className="table mb-0">
                <thead className="table-light">
                  <tr key={10}>

                    <th>{props.t("TypeofTrustService")}</th>
                    <th></th>
                    <th>{props.t("Amount")}</th>

                  </tr>
                </thead>
                <tbody>
                  <tr key={1}>
                    <td>{props.t("GuaranteeTrust")}</td>

                    <th scope="row">
                    </th>
                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="guaranteetrust"
                        type="number"
                        id="guaranteetrust"
                        onChange={(e) => { SumTotal() }}
                        value={props.ServiciosFiduciario?.guaranteetrust ?? ''}
                      />
                    </th>

                  </tr>

                  <tr key={2}>
                    <td>{props.t("AdministrationTrust")}</td>

                    <th scope="row">
                    </th>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="administrationTrust"
                        type="number"
                        id="administrationTrust"
                        onChange={(e) => { SumTotal() }}
                        value={props.ServiciosFiduciario?.administrationTrust ?? ''}
                      />
                    </th>

                  </tr>

                  <tr key={3}>
                    <td>{props.t("InvestmentTrust")}</td>

                    <th scope="row">
                    </th>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="investmentTrust"
                        type="number"
                        id="investmentTrust"
                        onChange={(e) => { SumTotal() }}
                        value={props.ServiciosFiduciario?.investmentTrust ?? ''}
                      />
                    </th>

                  </tr>

                  <tr key={4}>
                    <td>{props.t("EstateTrustAndWill")}</td>

                    <th scope="row">
                    </th>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="stateTrust"
                        type="number"
                        id="stateTrust"
                        onChange={(e) => { SumTotal() }}
                        value={props.ServiciosFiduciario?.stateTrust ?? ''}
                      />
                    </th>

                  </tr>


                  <tr key={5}>
                    <td>{props.t("EscrowAccount")}</td>

                    <th scope="row">
                    </th>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="scrowAccount"
                        type="number"
                        id="scrowAccount"
                        onChange={(e) => { SumTotal() }}
                        value={props.ServiciosFiduciario?.scrowAccount ?? ''}
                      />
                    </th>

                  </tr>

                  <tr key={6}>
                    <td>{props.t("OthersSpecify")}</td>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="description"
                        type="text"
                        id="description"
                        value={props.ServiciosFiduciario?.description ?? ''}
                      />
                    </th>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="others"
                        type="number"
                        id="others"
                        onChange={(e) => { SumTotal() }}
                        value={props.ServiciosFiduciario?.others ?? ''}
                      />
                    </th>

                  </tr>

                  <tr key={7}>
                    <td>TOTAL</td>

                    <th scope="row">
                    </th>

                    <th scope="row">
                      <AvField
                        className="form-control"
                        name="investmentTrust"
                        type="text"
                        id="investmentTrust"
                        disabled={true}
                        value={`$${Total}`}
                      />
                    </th>

                  </tr>

                </tbody>
              </Table>

            </div>
          </Col>
        </Row>
      </CardBody>

    </React.Fragment>
  );
}

export default (withTranslation()(DatosServicioFiduciario));
