                      className="form-control"
                      name="detail"
                      validation={{ required: true }}
                      type="textarea"
                      rows={7}
                      id="detail"
                      value={DataGeneral?.detail ?? ''} />
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="5">
                  <div className="mb-3">
                    <Label htmlFor="province">{t("Final destination of the disbursement is for Free Zone")}</Label>
                    {dutyFreeRender}
                  </div>
                </Col>
                {/* <Col md="3">
                  <div className="mb-3">
                    <Label htmlFor="field59">{t("SalesChannel")}</Label>
                    {saleChannelRender}
                  </div>
                </Col> */}
              </Row>

