import SweetAlert from "react-bootstrap-sweetalert";
import {
  ServicioExposicionCorporativa,
  ServicioResumenCambios,
  ServicioFacilidades,
  ServicioRefinanciamiento,
  ServicioChecklist,
  ServicioPropuestaCredito,
} from "../../../services/PropuestaCredito";
import CorporateExposureClient from "../../../components/PropuestaCreditoComercial/CorporateExposureClient";

