import PropTypes from 'prop-types';
import React, { useEffect, useState } from "react"
import { withTranslation } from "react-i18next"
import { Link } from "react-router-dom"
import {
    Table,
    Card,
    CardBody,
    Pagination,
    PaginationItem,
    PaginationLink,
    CardFooter,
    Button,
    Row,
    Col,
} from "reactstrap"
import ApiServicesDetalleDocumentacion from '../../../../services/DocumentacionLegal/ApiServicesDetalleDocumentacion';
import { BpmServices, BackendServices } from "../../../../services";
import { useLocation, useHistory } from "react-router-dom";
import * as url from "../../../../helpers/url_helper"
import SweetAlert from "react-bootstrap-sweetalert"
import { AvField, AvForm, AvGroup } from 'availity-reactstrap-validation';
import Switch from "react-switch";
import { useTranslation } from 'react-i18next';
import Currency from '../../../../helpers/currency';

const Offsymbol = () => {
    return (
        <div
            style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "100%",
                fontSize: 12,
                color: "#fff",
                paddingRight: 2,
            }}
        >
            {" "}
            No
        </div>
    );
};
const OnSymbol = () => {
    return (
        <div
            style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "100%",
                fontSize: 12,
                color: "#fff",
                paddingRight: 2,
            }}
        >
            {" "}
            Si
        </div>
    );
};
const estiloTotales = { backgroundColor: "lightgrey !important", color: "black !important", textAlign: "center" }
const Honorarios = (props) => {
    const [switch1, setswitch1] = useState(false);
    const [switch2, setswitch2] = useState(false);
    const [switch3, setswitch3] = useState(false);
    const [switch4, setswitch4] = useState(false);
    const [switch5, setswitch5] = useState(false);
    const [switch6, setswitch6] = useState(false);
    const [switch7, setswitch7] = useState(false);
    const [switch8, setswitch8] = useState(false);
    const [switch9, setswitch9] = useState(false);
    const [switch10, setswitch10] = useState(false);
    const [switch11, setswitch11] = useState(false);
    const [switch12, setswitch12] = useState(false);
    const [switch13, setswitch13] = useState(false);
    const [switch14, setswitch14] = useState(false);
    const { t, i18n } = useTranslation();
    const currencyData = new Currency();
    const apiServiceBackend = new BackendServices();
    const location = useLocation()
    const [openModalRiesgo, setopenModalRiesgo] = useState(false);
    const [facilidadesData, setfacilidadesData] = useState(null);
    const [recordsFacilidades, setrecordsFacilidades] = useState(null);
    const [dataRows, setdataRows] = useState(null);
    const [messageNotAvailable, setmessageNotAvailable] = useState(false);
    const [locationData, setLocationData] = useState(location.data);
    const history = useHistory();
    const jsonHonorarios = [
        {
            min: 0.01,
            max: 10000.01,
            amountAdd: 150.00,
            porcent: 7
        },
        {
            min: 10000.00,
            max: 50000.00,
            amountAdd: 200.00,
            porcent: 7
        },
        {
            min: 50000.00,
            max: 100000.00,
            amountAdd: 300.00,
            porcent: 7
        },
        {
            min: 100000.01,
            max: 250000.00,
            amountAdd: 350.00,
            porcent: 7
        },
        {
            min: 250000.01,
            max: 1000000.01,
            amountAdd: 500.00,
            porcent: 7
        },
        {
            min: 1000000.01,
            max: 2000000.00,
            amountAdd: 750.00,
            porcent: 7
        },
        {
            min: 2000000.01,
            max: 3000000.00,
            amountAdd: 1000.00,
            porcent: 7
        },
        {
            min: 3000000.01,
            max: 4000000.00,
            amountAdd: 1500.00,
            porcent: 7
        },
        {
            min: 4000000.01,
            max: 5000000.00,
            amountAdd: 2000.00,
            porcent: 7
        },
        {
            min: 5000000.01,
            max: 6000000.00,
            amountAdd: 2500.00,
            porcent: 7
        },
        {
            min: 6000000.01,
            max: 7000000.00,
            amountAdd: 3000.00,
            porcent: 7
        },
        {
            min: 7000000.01,
            max: 8000000.00,
            amountAdd: 3500.00,
            porcent: 7
        },
        {
            min: 8000000.01,
            max: 9000000.00,
            amountAdd: 4000.00,
            porcent: 7
        },
        {
            min: 9000000.01,
            max: 10000000.00,
            amountAdd: 4500.00,
            porcent: 7
        }
    ]
    React.useEffect(() => {
        let dataSession;
        if (location.data !== null && location.data !== undefined) {
            if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
                history.push(url.URL_DASHBOARD);
            } else {
                sessionStorage.setItem('locationData', JSON.stringify(location.data));
                setLocationData(location.data);
                dataSession = location.data;
            }
        } else {
            //chequeamos si tenemos guardado en session
            var result = sessionStorage.getItem('locationData');

            if (result !== undefined && result !== null) {
                result = JSON.parse(result);
                setLocationData(result);
                dataSession = result;
            }
        }
        initializeData(dataSession)
    }, [!locationData]);
    function initializeData(dataSession) {
        apiServiceBackend.consultGeneralDataPropCred(dataSession?.transactionId ?? dataSession?.transactionId ?? 0)
            .then((data) => {
                let requestId = data[0]?.requestId;
                apiServiceBackend.retrieveProposalType()
                    .then((propuesta) => {
                        apiServiceBackend.retrieveFacilityType()
                            .then((facilidad) => {
                                apiServiceBackend.consultarFacilidades(requestId).then(resp => {
                                    let jsonSet = [];
                                    let totales = {
                                        totalMonto: 0,
                                        totalFinal: 0
                                    };
                                    for (let i = 0; i < resp.length; i++) {
                                        let jsonAdd = jsonHonorarios.filter(x => resp[i].amount >= x.min && resp[i].amount <= x.max);
                                        totales.totalMonto = Number(parseFloat(totales.totalMonto)) + Number(resp[i].amount);
                                        totales.totalFinal = Number(parseFloat(totales.totalFinal)) + Number(parseFloat(jsonAdd[0]?.amountAdd + ((jsonAdd[0]?.porcent / 100) * resp[i].amount)).toFixed(2));
                                        jsonSet.push({
                                            amount: Number(resp[i].amount),
                                            facilityTypeId: resp[i].facilityTypeId,
                                            facilityTypeName: facilidad.find(x => x.id === resp[i].facilityTypeId)?.description,
                                            proposalTypeId: resp[i].proposalTypeId,
                                            proposalTypeName: propuesta.find(x => x.id === resp[i].proposalTypeId)?.description,
                                            debtor: resp[i].debtor,
                                            amountAdd: jsonAdd[0]?.amountAdd ?? 0,
                                            max: jsonAdd[0]?.max ?? 0,
                                            min: jsonAdd[0]?.min ?? 0,
                                            porcent: jsonAdd[0]?.porcent ?? 0,
                                            totalAmount: parseFloat(jsonAdd[0]?.amountAdd + ((jsonAdd[0]?.porcent / 100) * resp[i].amount)).toFixed(2),
                                        })
                                    }
                                    setrecordsFacilidades(totales)
                                    setfacilidadesData(jsonSet.map((data, index) => (<tr>
                                        <td>{data.facilityTypeName}</td>
                                        <td style={{textAlign: "center" }}>${currencyData.formatTable(data.amount ?? 0)}</td>
                                        {/* <td>${currencyData.formatTable(data.min ?? 0)}</td>
                                        <td>${currencyData.formatTable(data.max ?? 0)}</td>
                                        <td>{currencyData.formatTable(data.porcent ?? 0)}%</td> */}
                                        <td style={{textAlign: "center" }}>${currencyData.formatTable(data.totalAmount ?? 0)}</td>
                                    </tr>)))
                                    console.log(jsonSet);
                                })
                            });
                    });
            });
    }
    return (
        <React.Fragment>
            <AvForm>
                <Row>
                    <Col md="12">
                        <div className="table-responsive styled-table-div">
                            <Table className="table table-striped table-hover styled-table table">
                                <thead>
                                    <tr style={{ textAlign: "center" }}>
                                        <th>{t("Facility Type")}</th>
                                        <th>{t("Amount")}</th>
                                        <th>{t("Total")}</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {facilidadesData}
                                    {recordsFacilidades ?
                                        <tr style={estiloTotales}>
                                            <td><b>Total Montos</b></td>
                                            <td><b>${currencyData.formatTable(recordsFacilidades.totalMonto)}</b></td>
                                            <td><b>${currencyData.formatTable(recordsFacilidades.totalFinal)}</b></td>
                                        </tr> : null}
                                </tbody>
                            </Table>
                        </div>
                    </Col>
                </Row>
            </AvForm>
        </React.Fragment>
    );
}
export default Honorarios;
