import PropTypes from 'prop-types';
import { translationHelper } from '../../helpers';

import {
  Table,
  Card,
  CardBody,
  Row,
  Col,
  Label
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"


const FormCreditRisk = (props) => {

  const t = translationHelper('financial_report');

  return (
    <Card>
      <CardBody>
        <h4 className="card-title">{t("Conclusions")}</h4>
        <p className="card-title-desc"></p>
        <Row>
          <Col md="12">
            <AvGroup className="mb-3">
              <Label htmlFor="strengths">{t("Strengths")}</Label>
              <AvField
                className="form-control"
                name="strengths"
                type="textarea"
                id="strengths"
                value={props.strengths ?? ''}
                rows="8" />
            </AvGroup>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <AvGroup className="mb-3">
              <Label htmlFor="weakness">{t("Weakness")}</Label>
              <AvField
                className="form-control"
                name="weakness"
                type="textarea"
                id="weakness"
                value={props.weakness ?? ''}
                rows="8" />
            </AvGroup>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <AvGroup className="mb-3">
              <Label htmlFor="conclusion">{t("Recommendations")}</Label>
              <AvField
                className="form-control"
                name="conclusion"
                type="textarea"
                id="conclusion"
                value={props.conclusion ?? ''}
                rows="8" />
            </AvGroup>
          </Col>
        </Row>
      </CardBody>
    </Card>
  );
};

FormCreditRisk.propTypes = {
};

FormCreditRisk.defaultProps = {
};

export default FormCreditRisk;
