import React, { useState, useRef, useEffect } from "react"
import { useLocation, useHistory, Link } from 'react-router-dom'
import moment from "moment";
import classnames from "classnames"
import { CreditProposalProvider } from '../../../../components/PropuestaCreditoComercial/CreditProposalContext';
import * as url from "../../../../helpers/url_helper";

import {
    Row,
    Col,
    Button,
    Card,
    CardBody, CardHeader,
    CardFooter,
    TabPane,
    Nav,
    NavLink,
    TabContent,
    Table
} from "reactstrap"
import LoadingOverlay from "react-loading-overlay";
import { Tabs, Tab } from 'react-bootstrap';
// UI Components
import Breadcrumbs from "../../../../components/Common/Breadcrumb"
import LoadingIndicator from "../../../../components/UI/LoadingIndicator";
import SuccessMessage from "../../../../components/UI/SuccessMessage";
import ModalListaDesembolso from "../Desembolso/Components/ModalViewListDesembolso";
// Services
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import { useTranslation } from "react-i18next";
import HeaderSections from "../../../../components/Common/HeaderSections";

const DesembolsoLista = (props) => {
    const location = useLocation();
    const history = useHistory();
    const [usuarioProspecto, setusuarioProspecto] = useState(null);
    const [dataGlobal, setdataGlobal] = useState({
        requestId: null,
    });
    const [loading, setLoading] = useState(false);
    const [showSuccess, setShowSuccess] = useState(false);
    const [successSave_dlg, setsuccessSave_dlg] = useState(false);
    const [error_dlg, seterror_dlg] = useState(false);
    const [error_msg, seterror_msg] = useState("");
    const [context, setContext] = useState({});
    const [dataSet, setdataSet] = useState({});
    const [datosUsuario, setdatosUsuario] = useState(null);
    const [dataPignoracion, setdataPignoracion] = useState(null);
    const [dataRows, setdataRows] = useState(null);
    const [ModalPrevisualizardata, setModalPrevisualizardata] = useState(false);
    const [isActiveLoading, setIsActiveLoading] = useState(false); //loading de la pagina
    // Services
    // Component Refs
    const { t, i18n } = useTranslation();
    //On Mounting (componentDidMount)
    const [dataLocation, setdataLocation] = useState(undefined);
    const [dataFacilidad, setdataFacilidad] = useState(null);
    const [dataFacilidadSet, setdataFacilidadSet] = useState(null);
    const [idFacilidad, setidFacilidad] = useState(null);
    const [dataLinea, setdataLinea] = useState(null);
    const [dataDelete, setdataDelete] = useState(null);
    const [type, settype] = useState(null);

    const [dataGarantiaMueble, setdataGarantiaMueble] = useState(null);
    const [confirm_alert, setconfirm_alert] = useState(false)
    const [dataBienUsados, setdataBienUsados] = useState(null);
    const [dataBienMueble, setdataBienMueble] = useState(null);
    const [dataOtrasGarantias, setdataOtrasGarantias] = useState(null);


    const [locationData, setLocationData] = useState(null);
    //Servicios
    const [backendServices, setbackendServices] = useState(new BackendServices());
    const [bpmServices, setbpmServices] = useState(new BpmServices());
    //const [coreServices, setcoreServices] = useState(new CoreServices());
    const [activeTab, setactiveTab] = useState(1);

    const [mainDebtor, setmainDebtor] = useState(null);
    //On Mounting (componentDidMount)
    React.useEffect(() => {
        if (location.data !== null && location.data !== undefined) {
            if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
                //location.data.transactionId = 0;
                //checkAndCreateProcedure(location.data);
                history.push(url.URL_DASHBOARD);
            }
            else {
                sessionStorage.setItem('locationData', JSON.stringify(location.data));
                setLocationData(location.data);
                fetchData(location.data);
            }
        }
        else {
            //chequeamos si tenemos guardado en session
            var result = sessionStorage.getItem('locationData');
            if (result !== undefined && result !== null) {
                result = JSON.parse(result);
                setLocationData(result);
                fetchData(result);
            }
        }
    }, []);
    //Caraga Inicial de datos
    function fetchData(locationData) {

        //chequear si la tarea no ha sido iniciada
        bpmServices.checkAndStartTask(locationData)
            .then((iniresult) => {
                if (iniresult === false) {
                    history.push(url.URL_DASHBOARD);
                }
            })
        loadUserProspect(locationData.transactionId)

        loadData(locationData)
    }
    function toggleTab(tab) {
        if (activeTab !== tab) {
            if (tab >= 1 && tab <= 5) {
                setactiveTab(tab)
                window.scrollTo(0, 0)
            }
        }
    }
    function loadUserProspect(transactionId) {
        // consultarDeudorPrincipal
        backendServices.consultPrincipalDebtor(transactionId)
            .then((data) => {
                if (data !== undefined) {
                    setmainDebtor(data);
                }
            });
    }

    function loadData(data1) {
        let datosPrueba = [
            {
                "requestId": "XYZ972022",
                "facilityId": 22,
                "cr": "1",
                "amount": 1.0,
                "debtor": "1",
                "customerId": 1,
                "balance": 1.0,
                "proposedType": "1",
                "subproposalType": "1",
                "facilityType": "Facilidad 1",
                "purpose": "1",
                "sublimits": "1",
                "proposedRate": 1.0,
                "nonSubsidyRate": 1.0,
                "effectiveRate": 1.0,
                "feci": true,
                "deadlinesDays": 1.0,
                "descriptionDeadlines": "1",
                "ltv": 1.0,
                "financialConditions": "1",
                "environmentalRiskCategory": 1.0,
                "covenant": "1",
                "environmentalOpinion": "1",
                "financialCovenant": "1",
                "legalDocumentation": "1",
                "opinionRiskCredit": "1",
                "provision": "1",
                "applyEscrow": true
            },
            {
                "requestId": "XYZ972022",
                "facilityId": 4,
                "cr": "",
                "amount": 22.4,
                "debtor": "",
                "customerId": 1,
                "balance": 22.5,
                "proposedType": "",
                "subproposalType": "",
                "facilityType": "Facilidad 32",
                "purpose": "",
                "sublimits": "",
                "proposedRate": 22.5,
                "nonSubsidyRate": 22.5,
                "effectiveRate": 22.5,
                "feci": true,
                "deadlinesDays": 22.5,
                "descriptionDeadlines": "",
                "ltv": 22.5,
                "financialConditions": "",
                "environmentalRiskCategory": 22.5,
                "covenant": "",
                "environmentalOpinion": "",
                "financialCovenant": "",
                "legalDocumentation": "",
                "opinionRiskCredit": "",
                "provision": "",
                "applyEscrow": true
            }
        ]
        setdataFacilidad(datosPrueba.map((items, index) => (
            items.debtor != "  " ?
                <Tab onSelect={(e) => { cargarDatosGenerales(e) }} className="m-4" key={index} eventKey={index} title={items.facilityType}>
                </Tab> : null
        )));
        return
        setdataBienMueble(null)
        setdataLinea(null)
        setdataBienUsados(null)
        backendServices.consultPrincipalDebtor(data1.transactionId)
            .then((data) => {
                if (data !== undefined) {
                    setusuarioProspecto(data);
                }
            });
        backendServices.consultGeneralDataPropCred(data1.transactionId)
            .then((data) => {
                console.log(data);
                dataGlobal.requestId = data[0].requestId;
                setdataGlobal(dataGlobal);
                // consultarFacilidades
                backendServices.retrieveFacilityType()
                    .then((facilidad) => {
                        backendServices.consultarFacilidades(data[0].requestId).then(resp => {
                            if (resp.length > 0) {
                                setdataFacilidadSet(resp)
                                setidFacilidad(resp[0].facilityId)
                                cargarDatosListas(data1, resp[0].facilityId, data1.transactionId)
                                setdataFacilidad(resp.map((items, index) => (
                                    items.debtor != "  " ?
                                        <Tab onSelect={(e) => { cargarDatosGenerales(e) }} className="m-4" key={index} eventKey={index} title={facilidad.find(x => x.id === items.facilityType)?.description}>

                                        </Tab> : null
                                )));
                            }
                        });
                    });
            });
    }
    function cargarDatosListas(data, facilidad, transactId) {
        backendServices.getCreditLine(data?.transactionId ?? 0).then(resp => {
            if (resp.length > 0 && resp != undefined) {
                setdataLinea(resp.map((data) => (
                    data.status ?
                        <tr key={data.lineId}>
                            <td>{data.lineId}</td>
                            <td>{data.currency}</td>
                            <td>{data.riskCountry}</td>
                            <td>{data.province}</td>
                            <td>{data.economicActivity}</td>
                            <td>{data.proposalDate}</td>
                            <td>{data.approvalDate}</td>
                            <td>{data.dueDate}</td>
                            <td>{data.limitDate}</td>
                            <td>{data.additionalInfo}</td>
                            <td>$ {data.amount}</td>
                            <td>$ {data.maxTotal}</td>
                            <td style={{ textAlign: "right" }}>
                                <Link
                                    to={{
                                        pathname: '/creditocomercial/AdminDesembolso/MantenimientoLinea',
                                        data: { facilityId: facilidad ?? 0, transactionId: transactId ?? 0, dataGeneral: data },
                                    }}
                                >
                                    <i className="mdi mdi-border-color mdi-24px"></i>
                                </Link>
                                <Button type="button" color="link" onClick={(resp) => { setdataDelete(data); settype("LINEACREDITO"); setconfirm_alert(true) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
                            </td>
                        </tr> : null)
                ));
            } else {
                setdataLinea(
                    <tr key={1}>
                        <td colSpan="12" style={{ textAlign: 'center' }}></td>
                    </tr>);
            }
        })
        backendServices.getGuaranteeMoveableAsset(data?.transactionId ?? 0).then(resp => {
            if (resp.length > 0 && resp != undefined) {
                setdataBienMueble(resp.map((data) => (
                    data.status && data.facilityId == facilidad ?
                        <tr key={data.lineId}>
                            <td>{data.guaranteeNumber}</td>
                            <td>{data.creditId}</td>
                            <td>{data.disbursementNumber}</td>
                            <td>{data.currency}</td>
                            <td>{data.customerNumber}</td>
                            <td>{data.assignAmount}</td>
                            <td>{data.guaranteedAmount}</td>
                            <td>{data.initialValue}</td>
                            <td>{data.guaranteeType}</td>
                            <td>{data.assetCode}</td>
                            <td>{data.guaranteeCurrency}</td>
                            <td style={{ textAlign: "right" }}>
                                <Link
                                    to={{
                                        pathname: '/creditocomercial/AdminDesembolso/GarantiasMuebles',
                                        data: { facilityId: facilidad ?? 0, transactionId: transactId ?? 0, dataGeneral: data },
                                    }}
                                >
                                    <i className="mdi mdi-border-color mdi-24px"></i>
                                </Link>
                                <Button type="button" color="link" onClick={(resp) => { setdataDelete(data); settype("BIENMUEBLE"); setconfirm_alert(true) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
                            </td>
                        </tr> : null)
                ));
            } else {
                setdataBienMueble(
                    <tr key={1}>
                        <td colSpan="12" style={{ textAlign: 'center' }}></td>
                    </tr>);
            }
        })
        backendServices.getOtherGuarantees(data?.transactionId ?? 0).then(resp => {
            if (resp.length > 0 && resp != undefined) {
                setdataOtrasGarantias(resp.map((data) => (
                    data.status && data.facilityId == facilidad ?
                        <tr key={data.lineId}>
                            <td>{data.guaranteeNumber}</td>
                            <td>{data.attachedNumber}</td>
                            <td>{data.loanNumber}</td>
                            <td>{data.currency}</td>
                            <td>{data.customerNumber}</td>
                            <td>{data.asignedAmount}</td>
                            <td>{data.guaranteedType}</td>
                            <td>{data.initialValue}</td>
                            <td>{data.guaranteeType}</td>
                            <td>{data.ownerAsset}</td>
                            <td>{data.guaranteeCurrency}</td>
                            <td>{data.bank}</td>
                            <td>{data.agency}</td>
                            <td>{data.instrumentType}</td>
                            <td>{data.documentId}</td>
                            <td>{data.trustNumber}</td>
                            <td>{data.issuedDate}</td>
                            <td>{data.guaranteeIdd}</td>
                            <td>{data.guarantorName}</td>
                            <td>{data.entityName}</td>
                            <td>{data.organismCode}</td>
                            <td>{data.issuedCountry}</td>
                            <td style={{ textAlign: "right" }}>
                                <Link
                                    to={{
                                        pathname: '/creditocomercial/AdminDesembolso/OtrasGarantias',
                                        data: { facilityId: facilidad ?? 0, transactionId: transactId ?? 0, dataGeneral: data },
                                    }}
                                >
                                    <i className="mdi mdi-border-color mdi-24px"></i>
                                </Link>
                                <Button type="button" color="link" onClick={(resp) => { setdataDelete(data); settype("OTRAGARANTIA"); setconfirm_alert(true) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
                            </td>
                        </tr> : null)
                ));
            } else {
                setdataOtrasGarantias(
                    <tr key={1}>
                        <td colSpan="12" style={{ textAlign: 'center' }}></td>
                    </tr>);
            }
        })
        backendServices.getGuaranteeUsedNewEquipments(data?.transactionId ?? 0).then(resp => {
            if (resp.length > 0 && resp != undefined) {
                setdataBienUsados(resp.map((data) => (
                    data.status && data.facilityId == facilidad ?
                        <tr key={data.lineId}>
                            <td>{data.attachedNumber}</td>
                            <td>{data.loanNumber}</td>
                            <td>{data.currency}</td>
                            <td>{data.customerNumber}</td>
                            <td>{data.guaranteeAmount}</td>
                            <td>{data.guaranteeType}</td>
                            <td>{data.bank}</td>
                            <td>{data.agency}</td>
                            <td>{data.assetCode}</td>
                            <td>{data.dueDate}</td>
                            <td>{data.trust}</td>
                            <td>{data.trustDate}</td>
                            <td style={{ textAlign: "right" }}>
                                <Link
                                    to={{
                                        pathname: '/creditocomercial/AdminDesembolso/GarantiaEquiposNuevosyUsados',
                                        data: { facilityId: facilidad ?? 0, transactionId: transactId ?? 0, dataGeneral: data },
                                    }}
                                >
                                    <i className="mdi mdi-border-color mdi-24px"></i>
                                </Link>
                                <Button type="button" color="link" onClick={(resp) => { setdataDelete(data); settype("BIENUSADOS"); setconfirm_alert(true) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
                            </td>
                        </tr> : null)
                ));
            } else {
                setdataBienUsados(
                    <tr key={1}>
                        <td colSpan="12" style={{ textAlign: 'center' }}></td>
                    </tr>);
            }
        })
        backendServices.getPledge(data?.transactionId ?? 0).then(resp => {
            if (resp.length > 0 && resp != undefined) {
                setdataPignoracion(resp.map((data) => (
                    data.status && data.facilityId == facilidad ?
                        <tr key={data.lineId}>
                            <td>{data.guaranteeNumber}</td>
                            <td>{data.attachedNumber}</td>
                            <td>{data.loanNumber}</td>
                            <td>{data.currency}</td>
                            <td>{data.customerNumber}</td>
                            <td>{data.guaranteeAmount}</td>
                            <td>{data.guaranteeType}</td>
                            <td>{data.bank}</td>
                            <td>{data.agency}</td>
                            <td>{data.accountNumber}</td>
                            <td>{data.dueDate}</td>
                            <td style={{ textAlign: "right" }}>
                                <Link
                                    to={{
                                        pathname: '/creditocomercial/AdminDesembolso/Pignoracion',
                                        data: { facilityId: facilidad ?? 0, transactionId: transactId ?? 0, dataGeneral: data },
                                    }}
                                >
                                    <i className="mdi mdi-border-color mdi-24px"></i>
                                </Link>
                                <Button type="button" color="link" onClick={(resp) => { setdataDelete(data); settype("PIGNORACION"); setconfirm_alert(true) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
                            </td>
                        </tr> : null)
                ));
            } else {
                setdataPignoracion(
                    <tr key={1}>
                        <td colSpan="12" style={{ textAlign: 'center' }}></td>
                    </tr>);
            }
        })
    }
    function updateData(data) {
    }
    function deleteData(data) {
    }
    function cargarDatosGenerales(datos) {
        console.log(datos);
    }
    function removeBodyCss() {
        document.body.classList.add("no_padding")
    }
    function toogleModalListaDesembolso() {
        setModalPrevisualizardata(!ModalPrevisualizardata);
        removeBodyCss()
    }
    function handleSelect(key) {
        cargarDatosListas(dataLocation, dataFacilidadSet[key].facilityId, dataLocation.transactionId ?? 0)
        setidFacilidad(dataFacilidadSet[key].facilityId);
    }
    return (
        <React.Fragment>
            <CreditProposalProvider value={context}>
                <div className="page-content">
                    <Breadcrumbs title={t("Commercial Credit")} breadcrumbItem={t("Garantias por facilidad")} />
                    <Card>
                        <CardHeader>
                            <Row>
                                <Col>
                                    <h4 className="card-title">{t("Administration")}</h4>
                                    <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                                </Col>
                            </Row>
                            <Row>
                                <Col sm={6}>
                                    <h5 className="card-title">{mainDebtor != null ? (mainDebtor.typePerson === "1" ? (mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2) : (mainDebtor.name)) : ""} </h5>
                                </Col>
                                <Col sm={6}>
                                    <h5 className="card-title" style={{ textAlign: "right" }}>{t("Tramit")}:{" "}#{locationData?.transactionId}</h5>
                                </Col>
                            </Row>
                        </CardHeader>
                        <CardBody>
                            <Row>
                                <Col md={3}>
                                    <Nav pills className="flex-column">
                                        <NavLink className={classnames({ active: activeTab === 1 })}
                                            onClick={() => {
                                                toggleTab(1)
                                            }}>
                                            {t("LineofCredit")}
                                        </NavLink>
                                        <NavLink className={classnames({ active: activeTab === 2 })}
                                            onClick={() => {
                                                toggleTab(2)
                                            }}>
                                            {t("Garantias Muebles Nuevos Y Usados")}
                                        </NavLink>
                                        <NavLink className={classnames({ active: activeTab === 3 })}
                                            onClick={() => {
                                                toggleTab(3)
                                            }}>
                                            {t("Garantia bien mueble")}
                                        </NavLink>
                                        <NavLink className={classnames({ active: activeTab === 4 })}
                                            onClick={() => {
                                                toggleTab(4)
                                            }}>
                                            {t("Otras Garantias")}
                                        </NavLink>
                                        <NavLink className={classnames({ active: activeTab === 5 })}
                                            onClick={() => {
                                                toggleTab(5)
                                            }}>
                                            {t("Garatías pignoracion")}
                                        </NavLink>
                                    </Nav>
                                </Col>
                                <Col md={9}>
                                    <TabContent activeTab={activeTab} className="body">
                                        <TabPane tabId={1}>
                                            <HeaderSections title="LineofCredit" descri="ListLineofCredit" t={t} />
                                            <Table className="table mt-1" responsive>
                                                <thead className="table-light">
                                                    <tr>
                                                        <th><strong>{t("Numero de Linea o Limite")}</strong></th>
                                                        <th><strong>{t("Moneda")}</strong></th>
                                                        <th><strong>{t("Paies de Riesgo")}</strong></th>
                                                        <th><strong>{t("Provincia")}</strong></th>
                                                        <th><strong>{t("Actividad Economica")}</strong></th>
                                                        <th><strong>{t("Fecha de Propuesta")}</strong></th>
                                                        <th><strong>{t("Fecha de Aprobacion")}</strong></th>
                                                        <th><strong>{t("Fecha de Vencimiento")}</strong></th>
                                                        <th><strong>{t("Fecha de Limite")}</strong></th>
                                                        <th><strong>{t("Frecuencia de Revision")}</strong></th>
                                                        <th><strong>{t("Monto")}</strong></th>
                                                        <th><strong>{t("Total Maximo")}</strong></th>
                                                        <th><Link
                                                            to={{
                                                                pathname: '/creditocomercial/AdminDesembolso/CreacionLinea',
                                                                data: { facilityId: idFacilidad ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                                                            }}
                                                        >
                                                            <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
                                                        </Link></th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {dataLinea}
                                                </tbody>
                                            </Table>
                                        </TabPane>
                                        <TabPane tabId={2}>
                                            <Row>
                                                <Tabs defaultActiveKey="0" id="uncontrolled-tab-example" className="mb-3" onSelect={(e) => { handleSelect(e) }}>
                                                    {dataFacilidad}
                                                </Tabs>
                                            </Row>
                                            <HeaderSections title="Garantias Muebles Nuevos Y Usados" descri="" t={t} />
                                            <Table className="table mt-1" responsive>
                                                <thead className="table-light">
                                                    <tr>
                                                        <th><strong>{t("Numero de Adjunto")}</strong></th>
                                                        <th><strong>{t("Numero de Prestamo")}</strong></th>
                                                        <th><strong>{t("Moneda")}</strong></th>
                                                        <th><strong>{t("Numero de cliente")}</strong></th>
                                                        <th><strong>{t("Monto de la Garantia")}</strong></th>
                                                        <th><strong>{t("Tipos de Garantia")}</strong></th>
                                                        <th><strong>{t("Banco")}</strong></th>
                                                        <th><strong>{t("Agencia(Banca)")}</strong></th>
                                                        <th><strong>{t("Codigo del Bien Mueble")}</strong></th>
                                                        <th><strong>{t("Fecha de Vencimiento transacción")}</strong></th>
                                                        <th><strong>{t("No. Fideicomiso")}</strong></th>
                                                        <th><strong>{t("Fecha Emision Del Fideicomiso")}</strong></th>
                                                        <th><Link
                                                            to={{
                                                                pathname: '/creditocomercial/AdminDesembolso/GarantiaEquiposNuevosyUsados',
                                                                data: { facilityId: idFacilidad ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                                                            }}
                                                        >
                                                            <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
                                                        </Link></th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {dataBienUsados}
                                                </tbody>
                                            </Table>
                                        </TabPane>
                                        <TabPane tabId={3}>
                                            <Row>
                                                <Tabs defaultActiveKey="0" id="uncontrolled-tab-example" className="mb-3" onSelect={(e) => { handleSelect(e) }}>
                                                    {dataFacilidad}
                                                </Tabs>
                                            </Row>
                                            <HeaderSections title="Garantia bien mueble" descri="" t={t} />
                                            <Table className="table mt-1" responsive>
                                                <thead className="table-light">
                                                    <tr>
                                                        <th><strong>{t("ID de Garantia")}</strong></th>
                                                        <th><strong>{t("ID del Credito")}</strong></th>
                                                        <th><strong>{t("Numero de Prestamo o Desembolso")}</strong></th>
                                                        <th><strong>{t("Moneda")}</strong></th>
                                                        <th><strong>{t("Numero de cliente")}</strong></th>
                                                        <th><strong>{t("Monto Asignado en Garantia")}</strong></th>
                                                        <th><strong>{t("Monto Garantizado")}</strong></th>
                                                        <th><strong>{t("Valor inicial de la Garantia")}</strong></th>
                                                        <th><strong>{t("Tipo de Garantia")}</strong></th>
                                                        <th><strong>{t("Codigo del Bien Mueble")}</strong></th>
                                                        <th><strong>{t("Moneda de la Garantia")}</strong></th>
                                                        <th><Link
                                                            to={{
                                                                pathname: '/creditocomercial/AdminDesembolso/GarantiasMuebles',
                                                                data: { facilityId: idFacilidad ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                                                            }}
                                                        >
                                                            <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
                                                        </Link></th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {dataBienMueble}
                                                </tbody>
                                            </Table>
                                        </TabPane>
                                        <TabPane tabId={4}>
                                            <Row>
                                                <Tabs defaultActiveKey="0" id="uncontrolled-tab-example" className="mb-3" onSelect={(e) => { handleSelect(e) }}>
                                                    {dataFacilidad}
                                                </Tabs>
                                            </Row>
                                            <HeaderSections title="Otras Garantias" descri="" t={t} />
                                            <Table className="table mt-1" responsive>
                                                <thead className="table-light">
                                                    <tr>
                                                        <th><strong>{t("ID de Garantia")}</strong></th>
                                                        <th><strong>{t("ID del credito")}</strong></th>
                                                        <th><strong>{t("Numero de Prestamo o Dsembolso")}</strong></th>
                                                        <th><strong>{t("Moneda")}</strong></th>
                                                        <th><strong>{t("Numero de cliente")}</strong></th>
                                                        <th><strong>{t("Monto Asignado en Garantia")}</strong></th>
                                                        <th><strong>{t("Monto Garantizado")}</strong></th>
                                                        <th><strong>{t("Valor Inicial de la Garantia")}</strong></th>
                                                        <th><strong>{t("Tipo de Garantia")}</strong></th>
                                                        <th><strong>{t("Codigo de Bien (Subtipo Garantia)")}</strong></th>
                                                        <th><strong>{t("Moneda de la Garantia")}</strong></th>
                                                        <th><strong>{t("Banco")}</strong></th>
                                                        <th><strong>{t("Agencia(Banca)")}</strong></th>
                                                        <th><strong>{t("Tipo de Instrumento Financiero")}</strong></th>
                                                        <th><strong>{t("ID Documento")}</strong></th>
                                                        <th><strong>{t("Numero Fidecomiso")}</strong></th>
                                                        <th><strong>{t("Fecha de Emision del Fidecomiso")}</strong></th>
                                                        <th><strong>{t("Id de Garantia")}</strong></th>
                                                        <th><strong>{t("Nombre del Fiador")}</strong></th>
                                                        <th><strong>{t("Avaluador")}</strong></th>
                                                        <th><strong>{t("Codigo de Organismo")}</strong></th>
                                                        <th><strong>{t("Paises de Emision")}</strong></th>
                                                        <th><Link
                                                            to={{
                                                                pathname: '/creditocomercial/AdminDesembolso/OtrasGarantias',
                                                                data: { facilityId: idFacilidad ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                                                            }}
                                                        >
                                                            <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
                                                        </Link></th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {dataOtrasGarantias}
                                                </tbody>
                                            </Table>
                                        </TabPane>
                                        <TabPane tabId={5}>
                                            <Row>
                                                <Tabs defaultActiveKey="0" id="uncontrolled-tab-example" className="mb-3" onSelect={(e) => { handleSelect(e) }}>
                                                    {dataFacilidad}
                                                </Tabs>
                                            </Row>
                                            <HeaderSections title="Garatías pignoracion" descri="" t={t} />
                                            <Table className="table mt-1" responsive>
                                                <thead className="table-light">
                                                    <tr>
                                                        <th><strong>{t("ID de Garantia")}</strong></th>
                                                        <th><strong>{t("Numero de Adjunto")}</strong></th>
                                                        <th><strong>{t("Numero de Prestamo o Desembolso")}</strong></th>
                                                        <th><strong>{t("Moneda")}</strong></th>
                                                        <th><strong>{t("Numero de cliente")}</strong></th>
                                                        <th><strong>{t("Monto de la Garantia")}</strong></th>
                                                        <th><strong>{t("Tipo de Garantia")}</strong></th>
                                                        <th><strong>{t("Banco")}</strong></th>
                                                        <th><strong>{t("Agencia(Banca)")}</strong></th>
                                                        <th><strong>{t("No. de Cuenta o Deposito")}</strong></th>
                                                        <th><strong>{t("Fecha de Expiracion")}</strong></th>
                                                        <th><Link
                                                            to={{
                                                                pathname: '/creditocomercial/AdminDesembolso/Pignoracion',
                                                                data: { facilityId: idFacilidad ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                                                            }}
                                                        >
                                                            <i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i>
                                                        </Link></th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {dataPignoracion}
                                                </tbody>
                                            </Table>
                                        </TabPane>
                                    </TabContent>
                                </Col>
                            </Row>
                            <Row>
                                <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
                                    <Link
                                        to={{
                                            pathname: '/dashboard',
                                        }}
                                    >
                                        <Button
                                            id="btnNew"
                                            color="dark"
                                            type="button"
                                            style={{ margin: "5px" }}
                                            onClick={props.toggle}
                                            data-dismiss="modal"
                                        >
                                            <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                                            {t("Exit")}
                                        </Button>
                                    </Link>
                                </Col>
                            </Row>
                        </CardBody>
                    </Card>

                    {successSave_dlg ? (
                        <SweetAlert
                            success
                            title={t("SuccessDialog")}
                            onConfirm={() => {
                                setsuccessSave_dlg(false);
                                loadData(dataLocation)
                            }}
                        >
                            {t("SuccessSaveMessage")}
                        </SweetAlert>
                    ) : null}
                    {error_dlg ? (
                        <SweetAlert
                            error
                            title={t("ErrorDialog")}
                            onConfirm={() => {
                                seterror_dlg(false)
                            }}
                        >
                            {error_msg}
                        </SweetAlert>
                    ) : null}
                    {confirm_alert ? (
                        <SweetAlert
                            title={t("Areyousure")}
                            warning
                            showCancel
                            confirmButtonText={t("Yesdeleteit")}
                            confirmBtnBsStyle="success"
                            cancelBtnBsStyle="danger"
                            onConfirm={() => {
                                const apiBack = new BackendServices();
                                // eliminarClienteIGR
                                if (type == "BIENUSADOS") {
                                    apiBack.deleteGuaranteeUsedNewEquipments(dataDelete?.transactId ?? 0, dataDelete?.facilityId ?? 0, dataDelete?.guaranteeId ?? 0).then(resp => {
                                        if (resp.statusCode == "500") {
                                            setconfirm_alert(false)
                                            seterror_dlg(true)
                                        } else {
                                            setconfirm_alert(false)
                                            setsuccessSave_dlg(true)
                                        }
                                    }).catch(error => {
                                        setconfirm_alert(false)
                                        seterror_dlg(true)
                                    })
                                }
                                if (type == "LINEACREDITO") {
                                    apiBack.deleteCreditLine(dataDelete?.transactId ?? 0, dataDelete?.lineId ?? 0).then(resp => {
                                        if (resp.statusCode == "500") {
                                            setconfirm_alert(false)
                                            seterror_dlg(true)
                                        } else {
                                            setconfirm_alert(false)
                                            setsuccessSave_dlg(true)
                                        }
                                    }).catch(error => {
                                        setconfirm_alert(false)
                                        seterror_dlg(true)
                                    })
                                }
                                if (type == "BIENMUEBLE") {
                                    apiBack.deleteGuaranteeMoveableAsset(dataDelete?.transactId ?? 0, dataDelete?.facilityId ?? 0, dataDelete?.guaranteeId ?? 0).then(resp => {
                                        if (resp.statusCode == "500") {
                                            setconfirm_alert(false)
                                            seterror_dlg(true)
                                        } else {
                                            setconfirm_alert(false)
                                            setsuccessSave_dlg(true)
                                        }
                                    }).catch(error => {
                                        setconfirm_alert(false)
                                        seterror_dlg(true)
                                    })
                                }
                                if (type == "OTRAGARANTIA") {
                                    apiBack.deleteOtherGuarantees(dataDelete?.transactId ?? 0, dataDelete?.facilityId ?? 0, dataDelete?.guaranteeId ?? 0).then(resp => {
                                        if (resp.statusCode == "500") {
                                            setconfirm_alert(false)
                                            seterror_dlg(true)
                                        } else {
                                            setconfirm_alert(false)
                                            setsuccessSave_dlg(true)
                                        }
                                    }).catch(error => {
                                        setconfirm_alert(false)
                                        seterror_dlg(true)
                                    })
                                }
                                if (type == "PIGNORACION") {
                                    apiBack.deletePledge(dataDelete?.transactId ?? 0, dataDelete?.facilityId ?? 0, dataDelete?.guaranteeId ?? 0).then(resp => {
                                        if (resp.statusCode == "500") {
                                            setconfirm_alert(false)
                                            seterror_dlg(true)
                                        } else {
                                            setconfirm_alert(false)
                                            setsuccessSave_dlg(true)
                                        }
                                    }).catch(error => {
                                        setconfirm_alert(false)
                                        seterror_dlg(true)
                                    })
                                }

                            }}
                            onCancel={() => setconfirm_alert(false)}
                        >
                            {t("Youwontbeabletorevertthis")}
                        </SweetAlert>
                    ) : null}
                </div>
                {loading && (
                    <LoadingIndicator message={t("Please Wait")} />
                )}
                {showSuccess && (
                    <SuccessMessage title={t("Operation Complete")} message={t("Credit Proposal Saved")} />
                )}
            </CreditProposalProvider>
        </React.Fragment >
    );

}

export default DesembolsoLista;
