import { useState } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Card,
    Row,
    Col,
    Form,
    Container,
    Button
} from "react-bootstrap";
import Select from 'react-select'

import FormContainerQuoter from './FormContainerQuoter'
import FormQuoterTypeNatural from './FormQuoterTypeNatural';
import FormDataQuoter from './FormDataQuoter';
import FormCommonFields from './FormCommonFields';
import TableResultQuoter from './TableResultQuoter';

const FormQuoterScreen = () => {
    // hooks
    const { t, i18n } = useTranslation();
    // custom hooks
    const [form, setForm] = useState({})

    // handlers
    const getFormData = (data) => {
        console.group("Form data")
        console.info(data)
        console.groupEnd()
    }

    const testHandleSubmit = () => {
        console.log("Working!", form)
    }

    const handleChange = (evt) => {
        console.log("Working lifting state up");
        console.log("evt:", evt);
        setForm({
            ...form,
            [evt.target.name]: evt.target.value
        })

    }

    const handleKeyOnlyNum = (evt) => {
        const keyPress = evt.key;
        const regexOnlyNum = /^[0-9,.]+$/

        if (!regexOnlyNum.test(keyPress))
            // return;
            console.log('here!', evt.target.value);
    }

    return (
        <>
            <FormContainerQuoter>
                <Card>
                    <Card.Body>
                        <Card.Title>
                            {t("ClientData")}
                        </Card.Title>
                        <Row>
                            <FormCommonFields
                                onChange={handleChange}
                                form={form}
                            />
                        </Row>
                        <Row>
                            <FormQuoterTypeNatural
                                onChange={handleChange}
                                form={form}
                            />
                        </Row>
                        <Row>
                            <FormDataQuoter
                                onChange={handleChange}
                                form={form}
                            />
                        </Row>
                        <Row >
                            <Col className='d-grid mt-5'>
                                <Button
                                    size='lg'
                                    className='float-end'
                                    type='submit'
                                    onClick={testHandleSubmit}
                                >
                                    {t("Calculate")}
                                </Button>
                            </Col>
                        </Row>
                    </Card.Body>
                </Card>
            </FormContainerQuoter>
            <TableResultQuoter />
        </>
    )
}

export default FormQuoterScreen
