import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import 'antd/dist/antd.css';
// import './index.css';
import { Select } from 'antd';
//Import Flatepicker
import "flatpickr/dist/themes/material_blue.css";
import Flatpickr from "react-flatpickr";
import { CoreServices } from "../../../../../services";
import Select2 from "react-select";
const ModalDeudasLargoPlazo = (props) => {
  const { Option } = Select;
  const { t, i18n } = useTranslation();
  const [facilidadesTipoSelect, setfacilidadesTipoSelect] = useState(undefined);
  const [campoRequeridoFacilidades, setcampoRequeridoFacilidades] = useState(false);
  const [fianzaAccionistaSelect, setfianzaAccionistaSelect] = useState(undefined);
  const [campoRequeridoFianzaAccionista, setcampoRequeridoFianzaAccionista] = useState(false);
  const [options, setBancos] = useState([]);
  const [codigoBanco, setCodigoBanco] = useState(null);
  const [date, setdate] = useState(null);
  const [expirationDate, setexpirationDate] = useState(null);
  const [campoRequeridoBanco, setcampoRequeridoBanco] = useState(false);
  // Submitimos formulario para busqueda de clientes
  const facilidadesTipo = [{ label: "LCR", value: "1" }, { label: "LCP", value: "2" }]
  const fianzaAccionistaList = [{ label: "Fianza de Accionista", value: "1" }, { label: "Fianza de Accionista 2", value: "2" }]
  React.useEffect(() => {
    // Read Api Service data
    cargarBanco()
    if (props.tipo == "ECL") {
      setCodigoBanco(props.jsonSow.bank)
      setdate(props.jsonSow.date)
      setexpirationDate(props.jsonSow.expirationDate)
    }
    setcampoRequeridoFacilidades(false);
    setcampoRequeridoFianzaAccionista(false);
    setcampoRequeridoBanco(false);
  }, []);
  React.useEffect(() => {
    // Read Api Service data

    if (props.tipo == "ECL") {
      var defaultVal = null;
      setfacilidadesTipoSelect(undefined)
      setfianzaAccionistaSelect(undefined)
      try {
        if (facilidadesTipo.length > 0 && props.jsonSow.facilityType !== null && facilidadesTipoSelect === undefined) {
          defaultVal = facilidadesTipo.find(x => x.label === props.jsonSow.facilityType);
          if (defaultVal !== undefined) {
            setfacilidadesTipoSelect(defaultVal);
          }
        }
        if (fianzaAccionistaList.length > 0 && props.jsonSow.bail !== null && fianzaAccionistaSelect === undefined) {
          defaultVal = fianzaAccionistaList.find(x => x.label === props.jsonSow.bail);
          if (defaultVal !== undefined) {
            setfianzaAccionistaSelect(defaultVal);
          }
        }
      }
      catch (err) { }

    }
    setcampoRequeridoFacilidades(false);
    setcampoRequeridoFianzaAccionista(false);
    setcampoRequeridoBanco(false);
  }, [props]);
  function changeAll(e, tipo) {
    if (tipo == "bank") {
      setCodigoBanco(e);
    }
    if (tipo == "date") {
      setdate(e.target.value);
    }
    if (tipo == "expirationDate") {
      setexpirationDate(e.target.value);
    }
  }
  function handleSubmit(event, errors, values) {
    if (codigoBanco == null) {
      setcampoRequeridoBanco(true);
      return;
    } else {
      setcampoRequeridoBanco(false);
    }
    if (facilidadesTipoSelect == undefined) {
      setcampoRequeridoFacilidades(true);
      return;
    } else {
      setcampoRequeridoFacilidades(false);
    }
    if (fianzaAccionistaSelect == undefined) {
      setcampoRequeridoFianzaAccionista(true);
      return;
    } else {
      setcampoRequeridoFianzaAccionista(false);
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.bank = codigoBanco;
    values.date = date;
    values.expirationDate = expirationDate;
    values.facilityType = facilidadesTipoSelect.label;
    values.bail = fianzaAccionistaSelect.label;
    if (props.tipo == "CL") {
      props.guardarDatos(values, props.tipo);
    }
    if (props.tipo == "ECL") {
      values.debtId = props.jsonSow.debtId;
      values.status = true;
      props.actualizarDatos(values, props.tipo);
    }
  }
  function cargarBanco() {
    const api = new CoreServices();
    api.getBancosCatalogo()
      .then(response => {
        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        const optionGroup1 = [
          {
            label: t("Bank"),
            options: json,
          },

        ];
        setBancos(json);
      });
  }


  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("LongTermDebts")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-12">
                        <Label htmlFor="idBank">{t("Bank")}</Label>
                        <Select
                          showSearch
                          style={{ width: "100%" }}
                          placeholder={t("SearchtoSelect")}
                          optionFilterProp="children"
                          defaultValue={props.jsonSow.bank}
                          onChange={(e) => { changeAll(e, "bank") }}
                          filterOption={(input, option) =>
                            option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                          }
                        >
                          {options.length > 0 ?

                            options.map((item) => (
                              <Option value={item.label}>{item.label}</Option>
                            ))

                            : null}
                        </Select>
                      </div>
                    </Col>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="facilityType">{t("FacilityType")}</Label>
                        <Select2
                          onChange={(e) => { setfacilidadesTipoSelect(facilidadesTipo.find(x => x.label === e.label)); }}
                          options={facilidadesTipo}
                          id="facilityType"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={facilidadesTipoSelect}
                        />
                        {campoRequeridoFacilidades ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="amount">{t("Amount")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="amount"
                          value={props.jsonSow.amount}
                          id="amount"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="date">{t("GrantDate")}</Label>
                        <AvField
                          className="form-control"
                          type="date"
                          name="date"
                          id="date"
                          value={props.jsonSow.date}
                          onChange={(e) => { changeAll(e, "date") }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="expirationDate">{t("DueDate")}</Label>
                        <AvField
                          className="form-control"
                          type="date"
                          name="expirationDate"
                          id="expirationDate"
                          value={props.jsonSow.expirationDate}
                          onChange={(e) => { changeAll(e, "expirationDate") }}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="debitBalance1">{t("CurrentYear")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="debitBalance1"
                          value={props.jsonSow.debitBalance1}
                          id="debitBalance1"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="debitBalance2">1 {t("YearAgo")} </Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="debitBalance2"
                          value={props.jsonSow.debitBalance2}
                          id="debitBalance2"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="debitBalance3">2 {t("YearAgo")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="debitBalance3"
                          value={props.jsonSow.debitBalance3}
                          id="debitBalance3"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                        {/* <InputMask
                          mask="$ 999,999,999.99"
                          value={props.value}
                          className="form-control input-color"
                        /> */}
                      </div>
                    </Col>

                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="rate">{t("Rate")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="rate"
                          value={props.jsonSow.rate}
                          id="rate"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="fee">{t("Term") + "(" + t("days") + ")"}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="fee"
                          value={props.jsonSow.fee}
                          id="fee"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="bail">{t("Bond")}</Label>
                        <Select2
                          onChange={(e) => { setfianzaAccionistaSelect(fianzaAccionistaList.find(x => x.label === e.label)); }}
                          options={fianzaAccionistaList}
                          id="bail"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={fianzaAccionistaSelect}
                        />
                        {campoRequeridoFianzaAccionista ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="fundDestiny">{t("DestinationOfFunds")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="fundDestiny"
                          value={props.jsonSow.fundDestiny}
                          id="fundDestiny"
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "CL" ? t("Save") : t("Edit")}
                    </Button> : null}
                </CardFooter>
              </Card>
            </AvForm>


          </Col>
        </Row>


      </div>
    </Modal>
  );
};

ModalDeudasLargoPlazo.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default ModalDeudasLargoPlazo;
