import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"


const ModalCargaTrabajo = (props) => {
  const { t, i18n } = useTranslation();

  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.contractAmount = Number(values.contractAmount);
    values.pendingAmount = Number(values.pendingAmount);
    values.initialPlan = Number(values.initialPlan);
    values.endPlan = Number(values.endPlan);
    values.executedPercentage = Number(values.executedPercentage);
    values.percentageTobeExecuted = Number(values.percentageTobeExecuted);
    values.expectedExecution = Number(values.expectedExecution);
    if (props.tipo == "CARGATRABAJO") {
      props.saveData(values, props.tipo);
    }
    if (props.tipo == "ECARGATRABAJO") {
      values.cashFlowWorkloadId = props.jsonSow.cashFlowWorkloadId;
      values.status = true;
      props.updateData(values, props.tipo);
    }
  }


  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Workload")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>

        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="client">{t("Client")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="customer"
                          value={props.jsonSow.customer}
                          id="customer"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="projectName">{t("ProjectName")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="projectName"
                          value={props.jsonSow.projectName}
                          id="projectName"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                          }}
                        />
                      </div>

                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="publicOrganizationPrivateCompany">{t("PublicOrganizationPrivateCompany")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="organismType"
                          value={props.jsonSow.organismType}
                          id="organismType"
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="contractAmount">{t("ContractAmount")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="contractAmount"
                          value={props.jsonSow.contractAmount}
                          id="contractAmount"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="june">{t("PendinAmount")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="pendingAmount"
                          value={props.jsonSow.pendingAmount}
                          id="pendingAmount"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="startExecutionPeriod">{t("StartExecutionPeriod")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="initialPlan"
                          value={props.jsonSow.initialPlan}
                          id="initialPlan"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="endExecutionPeriod">{t("EndExecutionPeriod")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="endPlan"
                          value={props.jsonSow.endPlan}
                          id="endPlan"
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="percentExecuted">{t("PercentExecuted")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="executedPercentage"
                          value={props.jsonSow.executedPercentage}
                          id="executedPercentage"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="percentToExecute">{t("PercentToExecute")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="percentageTobeExecuted"
                          value={props.jsonSow.percentageTobeExecuted}
                          id="percentageTobeExecuted"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="expectedExecution">{t("ExpectedExecution")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          validate={{
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          name="expectedExecution"
                          value={props.jsonSow.expectedExecution}
                          id="expectedExecution"
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="assignedDomiciledContract">{t("AssignedDomiciledContract")}</Label>
                        <AvField
                          className="form-control"
                          type="text"

                          name="contractType"
                          value={props.jsonSow.contractType}
                          id="contractType"
                        />
                      </div>
                    </Col>
                  </Row>

                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>

                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "CARGATRABAJO" ? t("Save") : t("Save")}
                    </Button> : null}
                </CardFooter>
              </Card>


            </AvForm>


          </Col>
        </Row>


      </div>
    </Modal>
  );
};

ModalCargaTrabajo.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default ModalCargaTrabajo;
