import PropTypes from 'prop-types';
import { translationHelpers } from '../../../helpers/translation-helper';

import {
  Table,
  Card,
  CardBody,
} from "reactstrap"


const Balance = (props) => {

  const [t, c] = translationHelpers('commercial_credit', 'common');

  const { headers, items } = props;

  if (!items) {
    return null;
  }

  const dataRows = items.map((item, index) => (
    <tr key={'row-' + index}>
      <td>{ item[0] }</td>
      <td className="text-end">{ item[1] }</td>
      <td className="text-end">{ item[2] }</td>
      <td className="text-end">{ item[3] }</td>
    </tr>)
  );

  return (
    <Card>
      <CardBody>
        <h4 className="card-title clearfix">{ props.title }</h4>
        <p className="card-title-desc"></p>
        <div className="table-responsive mt-2">
          <Table className="table mb-0">
            <thead className="table-light">
              <tr>
                <th>{ headers[0] }</th>
                <th className="text-end">{ headers[1] }</th>
                <th className="text-end">{ headers[2] }</th>
                <th className="text-end">{ headers[3] }</th>
              </tr>
            </thead>
            <tbody>
              {dataRows}
            </tbody>
          </Table>
        </div>
      </CardBody>
    </Card>
  );
};

Balance.propTypes = {
  title: PropTypes.string,
  headers: PropTypes.array.isRequired,
  items: PropTypes.array.isRequired
};


export default Balance;
