  function SetterUseStateSelect({ SetData, data, SetGlobalCat, SetUseSelected, useSelected, structure }) {
    let json = data?.map($$ => ({ label: t($$[structure.description]), value: $$[structure.id] }));
    SetGlobalCat(json)
    try {
      var defaultVal = null;
      SetUseSelected(undefined)
      if (json?.length > 0 && useSelected === undefined) {
        defaultVal = json.find(x => x.value === SetData);
        if (defaultVal !== undefined) {
          SetUseSelected(defaultVal);
        }
        return defaultVal;
      }
    }
    catch (err) { console.log(err); }
  }


