    setidFacilidad(dataFacilidadSet[key].facilityId);
  }
  function check(e) {
    let tecla = (document.all) ? e.keyCode : e.which;
    //Tecla de retroceso para borrar, siempre la permite
    if (tecla == 45) {
      e.preventDefault();
      return true;
    }

    return false;
  }
  function dataProductFunction(data) {
    console.log(data);
    setdataProduct(data)
  }
  function dataLineaFunction(data) {
    console.log(data);
    setdataLinea(data)
  }
