import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalInformacionProveedores from "./ModalInformacionProveedores"
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"

const InformacionProveedores = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const [formValid, setFormValid] = useState(false);
  const location = useLocation()
  const [dataReturn, setDataReturn] = useState(props.dataInformacionProveedores);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [dataDelete, setDataDelete] = useState([]);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [tipoDato, settipoDato] = useState("");
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [dataAccionistasDataRows, setdataAccionistasDataRows] = useState(null)
  const [jsonProveedores, setjsonProveedores] = useState({
    name:null,
    oldRelationship:null,
    percentPurchases:null,
    creditDays:null,
    negotiationConditions:null,
    country:null
  });
  const dataAccionistas = [
    { id: 1, name: "Panafoto", country: "Panamá", buypercent: "10", ageofRelationship: "10", crtermindays: "30-120 días", description: '' },
    { id: 2, name: "Panafoto", country: "Panamá", buypercent: "10", ageofRelationship: "12", crtermindays: "30-120 días", description: '' },
    { id: 3, name: "Panafoto", country: "Panamá", buypercent: "10", ageofRelationship: "12", crtermindays: "30-120 días", description: '' },
    { id: 4, name: "Panafoto", country: "Panamá", buypercent: "10", ageofRelationship: "12", crtermindays: "30-120 días", description: '' },
  ];
  React.useEffect(() => {
    // Read Api Service data
    initializeData();
  }, []);
  function initializeData() {
    const api = new BackendServices()
    api.consultarListaProveedoresIGR(dataLocation.transactionId).then(resp => {
      if (resp.providers.length > 0) {
      dataReturn.dataTableInformacionProveedores = resp.providers;
      setDataReturn(dataReturn);
      setdataAccionistasDataRows(resp.providers.map((data) => (
        data.status?
        <tr key={data.providerId}>
          <td>{data.name}</td>
          <td>{data.country}</td>
          <td>{data.oldRelationship}</td>
          <td>{data.percentPurchases}</td>
          <td>{data.creditDays}</td>
          <td>{data.negotiationConditions}</td>
          <td style={{ textAlign: "right" }}>
            <Button type="button" color="link" onClick={(resp) => { actualizarDatosProveedor(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
            <Button type="button" color="link" onClick={(resp) => { EliminarProveedor(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
          </td>
        </tr>:null)
      ));
    }else{
      setdataAccionistasDataRows(
        <tr key={1}>
          <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
        </tr>);
    }
    });
  }
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    },dataReturn
  }));
  function gestionDatos(data, tipo) {
    const api = new BackendServices()
    data.transactId = dataLocation.transactionId;
    let datos={
      "transactId":Number(data.transactId),
      "name":data.name,
      "oldRelationship":Number(data.oldRelationship),
      "percentPurchases":Number(data.percentPurchases),
      "creditDays":Number(data.creditDays),
      "negotiationConditions":data.negotiationConditions,
      "country":data.country
  }
    if (tipo == "guardar") {      
      api.nuevoProveedorIGR(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      api.actualizarProveedorIGR(data).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  function verDatosProveedor(data) {
    setjsonProveedores(data)
    settipoDato("ver")
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function actualizarDatosProveedor(data) {
    setjsonProveedores(data)
    settipoDato("settipoDato")
    setbotonValidation(true);
    toggleShowModelAttachment()
  }
  function EliminarProveedor(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    //props.onSubmit(values);
  }


  return (
    <React.Fragment>
      <h5>
        {t("ProvidersInfo")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmProveedores" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Name")}</th>
                  <th>{t("Country")}</th>
                  <th>{t("AgeofRelationship")}</th>
                  <th>{t("BuyPercent")}</th>
                  <th>{t("Crtermindays")}</th>
                  <th>{t("SpecialTradingConditions")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonProveedores({
                      name:null,
                      oldRelationship:null,
                      percentPurchases:null,
                      creditDays:null,
                      negotiationConditions:null,
                      country:null
                    });settipoDato("guardar"); toggleShowModelAttachment();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataAccionistasDataRows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDescription">{t("Description")}</Label>
              <AvField
                className="form-control"
                name="description"
                type="textarea"
                value={props.dataInformacionProveedores.description}
                onChange={(e) => { dataReturn.description = e.target.value; setDataReturn(dataReturn); }}
                id="description"
                rows="7" />
            </div>
          </Col>
        </Row>
        <Row>
          <Col md="4">
            <AvGroup className="mb-3">
              <Label htmlFor="purchasingCycle">{t("PurchasingCycle")}</Label>
              <AvField
                className="form-control"
                name="purchasingCycle"
                type="text"
                value={props.dataInformacionProveedores.purchasingCycle}
                onChange={(e) => { dataReturn.purchasingCycle = e.target.value; setDataReturn(dataReturn); }}
                id="purchasingCycle"
                rows="7" />
            </AvGroup>
          </Col>
        </Row>
      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarProveedorIGR({ transactId: dataLocation.transactionId, providerId: dataDelete.providerId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalInformacionProveedores tipoDato={tipoDato} jsonProveedores={jsonProveedores} botones={botonValidation} gestionDatos={gestionDatos} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );

});

/*
InformacionProveedores.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
*/

export default InformacionProveedores;
