import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"
import {
  Row,
  Col,
  Button,
  Label,
  Table,
} from "reactstrap"
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import AvInput from 'availity-reactstrap-validation/lib/AvInput'
import ModalFacilidadActivosFijos from "./ModalFacilidadActivosFijos"
import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper"
import { BackendServices } from "../../../../../services"
import SweetAlert from "react-bootstrap-sweetalert"
const FacilidadActivosFijos = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const { locationData } = props;
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [dataReturn, setdataReturn] = useState(props.dataFacilidadActivosFijos);
  const [formValid, setFormValid] = useState(false);
  const [showModalFacilidadActivosFijos, setShowModalFacilidadActivosFijos] = useState(false);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [showBlock1, setShowBlock1] = useState(false);
  const [showBlock2, setShowBlock2] = useState(false);
  const [showBlock3, setShowBlock3] = useState(false);
  const [showBlock4, setShowBlock4] = useState(false);
  const [dataFixedAssetsFacilitiesRows, setdataFixedAssetsFacilitiesRows] = useState(null);
  const [tipo, setTipo] = useState("")
  const [dataFacilidad, setdataFacilidad] = useState({
    "transactId": 0,
    "address": null,
    "propertyType": {
      "code": "",
      "name": ""
    },
    "observations": null,
    "ownerCompany": null,
    "leaseFee": 0,
    "leaseConditions": null,
    "contractDuration": 0
  });
  const dataFixedAssetsFacilities = [
    { id: 1, address: "Galera Principal", description: "", propertytype: 'Propia', ownercompany: 'Empresas Carbone, S.A', leasefee: "2000", contractperiod: "", leaseterms: "" },
    { id: 2, address: "Galera Principal", description: "", propertytype: 'Alquilada', ownercompany: 'Empresas Carbone, S.A', leasefee: "2000", contractperiod: "", leaseterms: "" },
    { id: 3, address: "Edif. Carbone", description: "", propertytype: 'Propia', ownercompany: 'Adolki, S.A', leasefee: "2000", contractperiod: "", leaseterms: "" },
    { id: 4, address: "Edif. Carbone", description: "", propertytype: 'Alquilada', ownercompany: 'Adolki, S.A', leasefee: "2000", contractperiod: "", leaseterms: "" },
  ];
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmActivosFijos');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  React.useEffect(() => {
    // Read Api Service data   
    initializeData();
  }, []);
  React.useEffect(() => {
    // Read Api Service data   
    setShowBlock1(props.dataFacilidadActivosFijos.variationsAssets);
    setShowBlock2(props.dataFacilidadActivosFijos.manufacturingAgroCompanies);
    setShowBlock3(props.dataFacilidadActivosFijos.tradingCompany);
    setShowBlock4(props.dataFacilidadActivosFijos.transportDistributionFleet);
  }, [props]);
  function initializeData() {
    const api = new BackendServices();
    // consultarActivosFijosIGR
    api.consultFixedAssetsIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataFixedAssetsFacilitiesRows(resp.fixedAssets.map((data) => (
          <tr key={data.id}>
            <td>{data.address}</td>
            <td>{data.observations}</td>
            <td>{data.propertyType.code}</td>
            <td>{data.ownerCompany}</td>
            <td>{data.leaseFee}</td>
            <td>{data.contractDuration}</td>
            <td>{data.leaseConditions}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { updateDataFacility(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { deleteDataFacility(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setdataFixedAssetsFacilitiesRows(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    })
  }
  function updateDataFacility(data) {
    setdataFacilidad(data)
    setbotonValidation(true);
    setTipo("editar")
    toggleShowModalFacilidadActivosFijos()
  }
  function deleteDataFacility(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  // gestion de datos
  function dataManagament(data, tipo) {
    const api = new BackendServices()
    data.transactId = dataLocation.transactionId;
    let datos = {
      "transactId": Number(dataLocation.transactionId),
      "address": data.address,
      "propertyType": {
        "code": data.propertyType.name,
        "name": data.propertyType.name
      },
      "observations": data.observations,
      "ownerCompany": data.ownerCompany,
      "leaseFee": Number(data.leaseFee),
      "leaseConditions": data.leaseConditions,
      "rentAmount": 0,
      "contractDuration": Number(data.contractDuration)
    }
    if (tipo == "guardar") {
      // nuevoActivosFijosIGR
      api.newFixedAssetsIGR(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalFacilidadActivosFijos();
        } else {
          toggleShowModalFacilidadActivosFijos();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      datos.facilityAssetId = data.facilityAssetId;
      // actualizarActivosFijosIGR
      api.updateFixedAssetsIGR(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalFacilidadActivosFijos();
        } else {
          toggleShowModalFacilidadActivosFijos();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  //abrimos modal para adjunar archivos
  function toggleShowModalFacilidadActivosFijos() {
    setShowModalFacilidadActivosFijos(!showModalFacilidadActivosFijos);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    if (!showBlock1) {
      values.variationsAssetsObs = "";
    }
    if (!showBlock2) {
      values.productionLine = "";
      values.capacity = "";
      values.usedCapacity = "";
    }
    if (!showBlock3) {
      values.warehouseCapacity = "";
    }
    if (!showBlock4) {
      values.units = "";
      values.renovation = "";
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setdataReturn(values)
    setFormValid(true)
  }
  return (
    <React.Fragment>
      <h5>
        {t("FixedAssetsFacilities")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmActivosFijos" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col lg="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Address")}</th>
                  <th>{t("Description")}</th>
                  <th>{t("PropertyType")}</th>
                  <th>{t("OwnerCompany")}</th>
                  <th>{t("LeaseFee")}</th>
                  <th>{t("ContractPeriod")}</th>
                  <th>{t("LeaseTerms")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setTipo("guardar"); setdataFacilidad({
                      "transactId": 0,
                      "address": null,
                      "propertyType": {
                        "code": "",
                        "name": ""
                      },
                      "observations": null,
                      "ownerCompany": null,
                      "leaseFee": 0,
                      "leaseConditions": null,
                      "contractDuration": 0
                    }); toggleShowModalFacilidadActivosFijos();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th>
                </tr>
              </thead>
              <tbody>
                {dataFixedAssetsFacilitiesRows}
              </tbody>
            </Table>

          </Col>
        </Row>
        <Row>
          <Col md="12">
            <AvGroup check className="mb-3">
              <AvInput type="checkbox" name="variationsAssets" checked={props.dataFacilidadActivosFijos.variationsAssets} onChange={() => setShowBlock1(!showBlock1)} />
              <Label style={{ textAlign: "left" }} htmlFor="variationsAssets">{t("ThereSignificanVariationsFixedAssetsCompanies")}</Label>
            </AvGroup>
          </Col>
          {showBlock1 && (
            <Col md="12">
              <div className="mb-3">
                <Label style={{ textAlign: "left" }} htmlFor="variationsAssetsObs">{t("IndicateTypeInvestmentWasMadeAdvantagesCompanyInvestmentWasFinanced")}</Label>
                <AvField
                  type="textarea"
                  name="variationsAssetsObs"
                  id="variationsAssetsObs"
                  maxLength="1000"
                  rows="7"
                  validate={{
                    required: { value: true, errorMessage: t("Required Field") },
                  }}
                  value={props.dataFacilidadActivosFijos.variationsAssetsObs}
                />
              </div>
            </Col>
          )}
        </Row>
        <Row>
          <Col md="12">
            <AvGroup check className="mb-3">
              <AvInput type="checkbox" name="manufacturingAgroCompanies" checked={props.dataFacilidadActivosFijos.manufacturingAgroCompanies} onChange={() => setShowBlock2(!showBlock2)} />
              <Label style={{ textAlign: "left" }} htmlFor="manufacturingAgroCompanies">{t("HasManufacturingCompaniesAgroindustries")}</Label>
            </AvGroup>
          </Col>
          {showBlock2 &&
            <React.Fragment>
              <Col md="12">
                <div className="mb-3">
                  <Label style={{ textAlign: "left" }} htmlFor="typeInvestmentWasMade">{t("MainProductionLinesWhatDoesConsist")}</Label>
                  <AvField
                    type="textarea"
                    name="productionLine"
                    id="productionLine"
                    maxLength="1000"
                    rows="7"
                    validate={{
                      required: { value: true, errorMessage: t("Required Field") },
                    }}
                    value={props.dataFacilidadActivosFijos.productionLine}
                  />
                </div>
              </Col>
              <Col md="12">
                <div className="mb-3">
                  <Label style={{ textAlign: "left" }} htmlFor="installedCapacity">{t("Installed Capacity in units amount of finished products that the company can produce per period per year, per month, etc")}</Label>
                  <AvField
                    type="textarea"
                    name="capacity"
                    id="capacity"
                    maxLength="1000"
                    rows="7"
                    validate={{
                      required: { value: true, errorMessage: t("Required Field") },
                    }}
                    value={props.dataFacilidadActivosFijos.capacity}
                  />
                </div>
              </Col>
              <Col md="12">
                <div className="mb-3">
                  <Label style={{ textAlign: "left" }} htmlFor="percentCapacityUsed">{t("Percentage of capacity used number of finished products that are currently producing per year, per month, etc")}</Label>
                  <AvField
                    type="textarea"
                    name="usedCapacity"
                    id="usedCapacity"
                    maxLength="1000"
                    rows="7"
                    validate={{
                      required: { value: true, errorMessage: t("Required Field") },
                    }}
                    value={props.dataFacilidadActivosFijos.usedCapacity}
                  />
                </div>
              </Col>
            </React.Fragment>
          }
        </Row>
        <Row>
          <Col md="12">
            <AvGroup check className="mb-3">
              <AvInput type="checkbox" name="tradingCompany" checked={props.dataFacilidadActivosFijos.tradingCompany} onChange={() => setShowBlock3(!showBlock3)} />
              <Label style={{ textAlign: "left" }} htmlFor="tradingCompany">{t("Has Marketing Companies")}</Label>
            </AvGroup>
          </Col>
          {showBlock3 &&
            <React.Fragment>
              <Col md="12">
                <div className="mb-3">
                  <Label style={{ textAlign: "left" }} htmlFor="storageDistributionCapacity">{t("Storage and Distribution Capacity (Installed and percentage of use)")}</Label>
                  <AvField
                    type="textarea"
                    name="warehouseCapacity"
                    id="warehouseCapacity"
                    maxLength="1000"
                    rows="7"
                    validate={{
                      required: { value: true, errorMessage: t("Required Field") },
                    }}
                    value={props.dataFacilidadActivosFijos.warehouseCapacity}
                  />
                </div>
              </Col>
            </React.Fragment>
          }
        </Row>
        <Row>
          <Col md="12">
            <AvGroup check className="mb-3">
              <AvInput type="checkbox" name="transportDistributionFleet" checked={props.dataFacilidadActivosFijos.transportDistributionFleet} onChange={() => setShowBlock4(!showBlock4)} />
              <Label style={{ textAlign: "left" }} htmlFor="transportDistributionFleet">{t("It has a transportation / distribution fleet")}</Label>
            </AvGroup>
          </Col>
          {showBlock4 &&
            <React.Fragment>
              <Col md="12">
                <div className="mb-3">
                  <Label style={{ textAlign: "left" }} htmlFor="indicatenumberunits">{t("Indicate the number of units with which it has the age of said vehicles the average number of trips per month average costs of routine and preventive maintenance of each vehicle and the frequency in which they do it")}</Label>
                  <AvField
                    type="textarea"
                    name="units"
                    id="units"
                    maxLength="1000"
                    rows="7"
                    validate={{
                      required: { value: true, errorMessage: t("Required Field") },
                    }}
                    value={props.dataFacilidadActivosFijos.units}
                  />
                </div>
              </Col>
              <Col md="12">
                <div className="mb-3">
                  <Label style={{ textAlign: "left" }} htmlFor="estimateashorttermrenewal">{t("Do you estimate a short-term renewal of the fleet? How do you plan to finance it?")}</Label>
                  <AvField
                    type="textarea"
                    name="renovation"
                    id="renovation"
                    maxLength="1000"
                    rows="7"
                    validate={{
                      required: { value: true, errorMessage: t("Required Field") },
                    }}
                    value={props.dataFacilidadActivosFijos.renovation}
                  />
                </div>
              </Col>
            </React.Fragment>
          }
        </Row>
        <AttachmentFileCore locationData={{ instanceId: locationData.instanceId, processId: locationData.processId, activityId: OPTs.ACT_FACILIDADACTIVOSFIJOS }} />
      </AvForm>
      <ModalFacilidadActivosFijos dataManagament={dataManagament} botones={botonValidation} tipo={tipo} dataFacilidad={dataFacilidad} isOpen={showModalFacilidadActivosFijos} toggle={() => { toggleShowModalFacilidadActivosFijos() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            // eliminarActivosFijosIGR
            apiBack.removeFixedAssetsIGR({ transactId: Number(dataLocation.transactionId), fixedAssetId: dataDelete.facilityAssetId }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );

});
FacilidadActivosFijos.propTypes = {
  onSubmit: PropTypes.func.isRequired,
  locationData: PropTypes.any,
}
export default FacilidadActivosFijos;
