        var result = await this.post(url.URL_CORE_DOCUMENTS, data,config);

        console.log("Result Document: ", result);

        /*
        var result = {
            "Status": {
                "StatusCode": "M0000",
                "StatusDesc": "OK"
            },
            "Token": "e793-ddb2-ab0e-6626-5d3e-5f19-b79b-1f5d",
