import React, { useEffect, useRef } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Row,
    Col,
    Form,
    Container,
    Card,
    Button,
} from "react-bootstrap";
import Select from 'react-select'
import { AvField, AvForm, AvGroup } from 'availity-reactstrap-validation';
import { Label } from 'reactstrap';
import Flatpickr from "react-flatpickr";

import checkNumber from '../../../helpers/checkNumber';
import * as OPTs from "../../../helpers/options_helper"
import { useFormQuoter } from './Hooks/useFormQuoter';
import LoadingOverlay from 'react-loading-overlay';
import TableResultQuoter from './TableResultQuoter';
import {
    optionsPersonType,
    optionsConfirmation,
    optionsGracePeriodType,
    optionsIdType,
    optionsNationality,
    optionsTypeLoan,
    optionsPayments,
    optionsBanking,
} from './dummy/optionsSelect'

const initialForm = {
    // // common fields
    personType: "Natural",
    idType: "",
    idnumber: "",
    // // client data
    // name: "",
    firstname: "",
    secondname: "",
    firstlastname: "",
    secondlastname: "",
    birthDate: "",
    nationality: "",
    phoneNumber: "",
    email: "",
    // // quoter data
    loanType: "compuesto",
    amountDebt: 0,
    anualRate: 0,
    effectiveRate: 0,
    paymentPeriod: 1,
    definedPayment: "No",
    customerDefinedPayment: 0,
    term: 0,
    feciPercentage: "No",
    feciRate: 0,
    gracePeriodSelect: "No",
    gracePeriodType: "p",
    gracePeriod: 0,
    // banking: "",
    // legalExpenses: 0,
    // financialTrust: 0,
}

export const FormData = (props) => {
    const { t, i18n } = useTranslation();

    const {
        clientExist
    } = props

    const formPrefill = { ...clientExist[0] }

    console.log("formPrefill:", formPrefill);

    const {
        form,
        hdlTest,
        hdlOnKeyUpRealNumber,
        hdlOnKeyUpPercentageNumber,
        hdlOnSubmit,
        hdlOnInvalidSubmit,
        hdlOnValidSubmit,
        hdlOnChange,
        assignSelectionFields,
        disableFeci,
        disableDefinedFee,
        disableTerm,
        disableGracePeriod,
        loading,
        response,
    } = useFormQuoter(initialForm)
    // console.log("form:", form);

    const optionsFormatDate = {
        dateFormat: OPTs.FORMAT_DATE,
        maxDate: new Date().setFullYear(new Date().getFullYear() - 18),
        // defaultDate: selectClient !== undefined ? new Date(moment(selectClient.birthDate, 'YYYY-MM-DD').format()) : new Date()
    }

    return (
        <>
            <AvForm
                // onSubmit={hdlOnValidSubmit}
                onValidSubmit={hdlOnValidSubmit}
                onInvalidSubmit={hdlOnInvalidSubmit}
            >
                <Card>
                    <Container>
                        <Card.Title className='mt-3'>
                            {t("ClientData")}
                        </Card.Title>
                        <Row>
                            <Col md={6} lg={4}>
                                <div className="mb-3">
                                    <Label htmlFor="personType">{t("PersonType")}</Label>
                                    <Select
                                        options={optionsPersonType}
                                        id="personType"
                                        name="personType"
                                        defaultValue={optionsPersonType[0]}
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={4}>
                                <div className="mb-3">
                                    <Label htmlFor="idType">{t("IdType")}</Label>
                                    <Select
                                        id="idType"
                                        name="idType"
                                        options={optionsIdType}
                                        defaultValue={optionsIdType[2]}
                                        value={form.name}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={12} lg={4}>
                                <div className="mb-3">
                                    <Label htmlFor="idnumber">{t("IdNumber")}</Label>
                                    <AvField
                                        id="idnumber"
                                        name="idnumber"
                                        value={formPrefill?.idnumber ? formPrefill.idnumber : form.name}
                                        validate={{ required: { value: true } }}
                                        errorMessage={t("Required Field")}
                                        options={optionsIdType}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                        </Row>
                        <Row>
                            {
                                form.personType === "Natural"
                                &&
                                <>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="firstname">{t("FirstName")}</Label>
                                            <AvField
                                                type='text'
                                                id="firstname"
                                                name="firstname"
                                                value={formPrefill?.firstname ? formPrefill.firstname : form.name}
                                                validate={{ required: { value: true } }}
                                                errorMessage={t("Required Field")}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="secondname">{t("SecondName")}</Label>
                                            <AvField
                                                id="secondname"
                                                name="secondname"
                                                value={formPrefill?.secondname ? formPrefill.secondname : form.name}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="firstlastname">{t("FirstLastName")}</Label>
                                            <AvField
                                                id="firstlastname"
                                                name="firstlastname"
                                                value={formPrefill?.lastname ? formPrefill.lastname : form.name}
                                                validate={{ required: { value: true } }}
                                                errorMessage={t("Required Field")}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                    <Col md={6} lg={3}>
                                        <div className="mb-3">
                                            <Label htmlFor="secondlastname">{t("SecondLastName")}</Label>
                                            <AvField
                                                id="secondlastname"
                                                name="secondlastname"
                                                value={formPrefill?.secondlastname ? formPrefill.secondlastname : form.name}
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </Col>
                                </>
                            }
                        </Row>
                        <Row>
                            {
                                form.personType === "Natural"
                                &&
                                <Col md={6} lg={3}>
                                    <AvGroup>
                                        <div className="mb-3">
                                            <Label htmlFor="birthDate">{t("Date of birth")}</Label>
                                            <Flatpickr
                                                id="birthDate"
                                                name="birthDate"
                                                value={formPrefill?.birthdate ? formPrefill.birthdate : form.name}
                                                placeholder={OPTs.FORMAT_DATE_SHOW}
                                                options={optionsFormatDate}
                                                className="form-control d-block"
                                                onChange={hdlOnChange}
                                            />
                                        </div>
                                    </AvGroup>
                                </Col>
                            }
                            {
                                form.personType !== "Natural"
                                &&
                                <Col md={6} lg={3}>
                                    <div className="mb-3">
                                        <Label htmlFor="name">{t("SocialReason")}</Label>
                                        <AvField
                                            type='text'
                                            id="name"
                                            name="nameJ"
                                            value={formPrefill?.businessName ? formPrefill.businessName : form.name}
                                            validate={{ required: { value: true } }}
                                            errorMessage={t("Required Field")}
                                            onChange={hdlOnChange}
                                        />
                                    </div>
                                </Col>
                            }
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="nationality">{t("Nationality")}</Label>
                                    <Select
                                        id="nationality"
                                        name="nationality"
                                        value={form.name}
                                        options={optionsNationality}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="phoneNumber">{t("PhoneNumber")}</Label>
                                    <AvField
                                        id="phoneNumber"
                                        name="phoneNumber"
                                        value={formPrefill?.phoneNumber ? formPrefill.phoneNumber : form.name}
                                        onKeyPress={(e) => { return checkNumber(e) }}
                                        onChange={hdlOnChange}
                                    />
                                </div>
                            </Col>
                            <Col md={6} lg={3}>
                                <div className="mb-3">
                                    <Label htmlFor="email">{t("Email")}</Label>
                                    <AvField
                                        type="email"
                                        id="email"
                                        name="email"
