import PropTypes from 'prop-types';
import { translationHelper } from '../../helpers';

import {
  Table,
  Card,
  CardBody,
  Row,
  Col,
} from "reactstrap"

import Budgets from './capex/Budgets';
import Projects from './capex/Projects';
import Sources from './capex/Sources';
import AttachmentList from '../Attachments/AttachmentList';



const Capex = (props) => {

  const t = translationHelper('financial_report');

  function handleNewAttachment(attachment) {

  }


  return (
    <Card>
      <CardBody>
        <h4 className="card-title">{ t("Capex.Title") }</h4>
        <p className="card-title-desc"></p>

        <Row className="mb-4">
          <Col lg="12">
            <Sources items={ props.sources } />
          </Col>
        </Row>

        <Row className="mb-4">
          <Col lg="12">
            <Budgets items={ props.budgets } />
          </Col>
        </Row>

        <Row className="mb-4">
          <Col lg="12">
            <Projects items={ props.projects } />
          </Col>
        </Row>

        <Row className="mb-4">
          <Col lg="12">
            <AttachmentList editMode={ true } attachments={ props.attachments } onAttach={ handleNewAttachment } />
          </Col>
        </Row>


      </CardBody>
    </Card>
  );
};

Capex.propTypes = {
  sources: PropTypes.array,
  budgets: PropTypes.array,
  projects: PropTypes.array,
};

Capex.defaultProps = {
  sources: [],
  budgets: [],
  projects: []
};

export default Capex;
