import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
  InputGroup,
} from "reactstrap"
import 'antd/dist/antd.css';
// import './index.css';
import { Select } from 'antd';
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { CoreServices } from "../../../../../services";
const ModalOtrosBancosActivosPasivos = (props) => {
  const { Option } = Select;
  const { t, i18n } = useTranslation();
  const [campoRequeridoBanco, setcampoRequeridoBanco] = useState(false);
  const [options, setBancos] = useState([]);
  const [codigoBanco, setCodigoBanco] = useState(null);
  // Submitimos formulario para busqueda de clientes
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.bank = codigoBanco;
    if(props.tipo=="OTROBANCO"){
      props.guardarDatos(values,props.tipo);
    }else{
      values.otherBankId=props.jsonSow.otherBankId;
      values.status=true;
      props.actualizarDatos(values,props.tipo);
    }
  }
  React.useEffect(() => {
    // Read Api Service data
    cargarBanco()
    if (props.tipo == "ECP") {
      setCodigoBanco(props.jsonSow.bank)
    }
    setcampoRequeridoBanco(false);
  }, []);
  function changeAll(e, tipo) {
    if (tipo == "bank") {
      setCodigoBanco(e);
    }
  }
  function cargarBanco() {
    const api = new CoreServices();
    api.getBancosCatalogo()
      .then(response => {
        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        const optionGroup1 = [
          {
            label: t("Bank"),
            options: json,
          },

        ];
        setBancos(json);
      });
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("OtherBanksLiabilitiesAssets")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="idBank">{t("Bank")}</Label>
                        <Select
                          showSearch
                          style={{ width: "100%" }}
                          placeholder={t("SearchtoSelect")}
                          optionFilterProp="children"
                          defaultValue={props.jsonSow.bank}
                          onChange={(e) => { changeAll(e, "bank") }}
                          filterOption={(input, option) =>
                            option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                          }
                        >
                          {options.length > 0 ?

                            options.map((item) => (
                              <Option value={item.value}>{item.label}</Option>
                            ))

                            : null}
                        </Select>
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="passive">%{t("Passive")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="passivePercentage"
                          value={props.jsonSow.passivePercentage}
                          id="passivePercentage"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="participation">{t("Participation")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="participation"
                          value={props.jsonSow.participation}
                          id="participation"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="longTerm">{t("LongTerm")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="longTerm"
                          value={props.jsonSow.longTerm}
                          id="longTerm"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="shortTerm">{t("ShortTerm")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="shortTerm"
                          value={props.jsonSow.shortTerm}
                          id="shortTerm"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "OTROBANCO"? t("Save") : t("Edit")}
                    </Button> : null}
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal>
  );
};

ModalOtrosBancosActivosPasivos.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func
};

export default (withTranslation()(ModalOtrosBancosActivosPasivos));
